css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix( https://youtube.com), 
    url-prefix( https://youtube.com),
    url-prefix( http://www.youtube.com), 
    url-prefix( http://youtube.com) {


#watch7-video .html5-video-controls, #watch7-video .html5-video-controls * {
    outline: medium none !important;
    -moz-user-select: none !important;
    -webkit-user-select: none !important;
   
}

.yt-uix-expander-head {
    
}

#watch7-video .progress-text {
    cursor: default;
}

#gb, #gbx3, #gbx4 {
    height: 0 !important;
    display: none !important;
}

yt-masthead-user-displayname {
    font-weight: bold !important;
}

/* Pasek główny */

#yt-masthead-container {
    padding: 0 !important;
}

#masthead-search-terms, #masthead-upload-button-group .yt-uix-button.yt-uix-sessionlink.start.yt-uix-button-hh-default {
    border: 0 !important;
    border-left: 1px dashed rgb(195, 195, 195) !important;
    border-right: 1px dashed rgb(195, 195, 195) !important;
    box-shadow: none !important;
    border-radius: 0 !important;
}

#search-btn, #masthead-upload-button-group .end.yt-uix-button.yt-uix-button-hh-default.yt-uix-button-empty, #yt-masthead-user-settings {
    border: 0 !important;
    border-left: 0px dashed rgb(195, 195, 195) !important;
    border-right: 1px dashed rgb(195, 195, 195) !important;
    box-shadow: none !important;
    border-radius: 0 !important;  
}

#masthead-upload-button-group .end.yt-uix-button.yt-uix-button-hh-default.yt-uix-button-empty, #yt-masthead-user-settings {
    border-left: 1px dashed rgb(195, 195, 195) !important;
    border-radius: 0 !important;  
}

#yt-masthead #logo-container, #masthead-search, #masthead-upload-button-group, #yt-masthead-user, #masthead-search { /* logo, pasek wyszukiwarki upload */
    margin-top: 0 !important;
    border-radius: 0 !important;
}

#logo {
    transform: scale(0.72) !important;
}

#yt-masthead a.yt-uix-button {
    padding-top: 2px !important;
}

#masthead-search-terms {
    margin: 0 !important;
    border-radius: 0 !important;
    background-color: rgb(253, 253, 253) !important;
}

}











/*****************       Wciśnięty duperel  ********/








@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://iitv.info) {
    .other_hosting {
        position: absolute !important;
        top: 117px !important;
        box-shadow: 3px 2px 3px rgba(0, 50, 120, .4) !important;
        background: rgb(210, 210, 210) !important;
    }
    .other_hosting, .other_hosting * {
        height: 410px !important;
        width: 740px !important;
    }
    .other_hosting .gobutton {
        background: none !important;
        position: relative;
        top: -433px;
        z-index: 9999;
    }
    .other_hosting:before {
        content: "Wybierz HOSTING" !important;
        color: rgb(120, 120, 120) !important;
        font-weight: bold !important;
        font-size: 150px !important;
        cursor: pointer !important;
    }
    
    #fb, #fb_2, #ebola, #p_48_c_68018B9B8BE64824, .play.hd3d, #prox_rek, #rek_pp,
    .play.proxof, .play.streamo, .play.maxvideo, .play.sprocked, #image_iitv,
    .player_kind, .rek_pp {
        display: none !important;
    }
    #players {
        clear: none !important;
    }
    
    #content, #episode_menu, #menu {
        float: right !important;
    }
    
    #episode_menu, #menu {
        margin-right: 0 !important;
        margin-left: 15px !important;
    }
    
    .episode_player h2 {
        width: auto !important;
        margin-top: 7px !important;
        margin-left: 9px !important;
    }
    
    .player_kind_line, #players br {
        display: none !important;
    }
    
    .episode_player #episode_title {
        float: none !important;
        width: 730px !important; 
    }


    .episode_player #episode_title a {
        margin-left: 15px !important;
    }
}
