css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(tumblr.com) {
.new_post_label .new_post_label_icon{
    background-image:none;
    background-size:513px 190px;
    background-repeat:no-repeat;
    position:absolute;
    display:block;
    width:76px;
    height:90px;
    top:5px;
    left:0px;
    opacity:0;
}
 
#posts .post.new_post .new_post_label {
    text-transform:lowercase;
    font-variant:small-caps;
    text-shadow:0.1em 0.1em rgba(0,0,0,1);
    margin-top:15px;
}


/* Code repaired by admin. See http://forum.userstyles.org/discussion/37591/attempting-to-mass-fix-broken-tumblr-dash-icon-styles */
.new_post {
	background: #ffffff url('http://i46.tinypic.com/16a9x61.png') center center no-repeat !important;
}
.new_post_label_icon {
	background-image: none !important;
}

ol#posts li.new_post{
background-color:#ffffff !important;
}



a#new_post_label_text, a#new_post_label_photo, a#new_post_label_quote, a#new_post_label_link, a#new_post_label_chat, a#new_post_label_audio, a#new_post_label_video{
color:#efefef !important;
}
}
