css:
@-moz-document url-prefix(http://www.tumblr.com/),url-prefix(https://www.tumblr.com/){


/* ======= Background ======= */

#dashboard_index, #dashboard_edit_post, #dashboard_submissions, #dashboard_drafts,#dashboard_post_queue, #dashboard_followers, #dashboard_members{background: url(/*[[bgimage]]*/) fixed !important;}

#dashboard_index div.l-content, #dashboard_submissions div.l-content, #dashboard_drafts div.l-content,#dashboard_post_queue div.l-content, #dashboard_members #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background: rgba(0,0,0,0) !important}






/* ======= Dashboard Style ======= */


#left_column #posts .notification .avatar {opacity:0.7 !important;}
#left_column #posts .notification .avatar:hover{opacity:1 !important;}

#right_column{background-color: #FFFFFF opacity:1 !important;}

#new_post_notice_container{color:#fff!important}


#posts .notification {
	opacity:0.8 !important;
	background:  #163156 !important;
	border: 1px #000000 solid !important;
	margin: -1px;
	}

#posts .notification .nipple.border, #posts .notification.alt .nipple.border {
	border-right: 7px solid #000000 !important;
	}

#posts .notification .nipple, #posts .notification.alt .nipple {
	border-right: 6px solid #163056 !important;
	}





/* ======= Time-cracked Logo ======= */


#logo > .logo_anchor {height: 0 !important;
	width: 0 !important;
	padding-left: 240px !important;
	padding-top: 70px !important;
	background: url(http://24.media.tumblr.com/tumblr_mam3e37V3f1qa0t06o1_250.png) no-repeat !important;}

#logo {
  overflow: visible !important;
}




/* ======= "Back to Top" Button ======= */

#return_to_top .return_to_top_icon{
background: url('http://25.media.tumblr.com/tumblr_mam3e37V3f1qa0t06o7_100.png') no-repeat transparent!important}




/* ======= Top Icons ======= */


#popover_blogs .tab_notice:after{display:none; }

div.l-header .tab.iconic > a {
		background-image:url('http://25.media.tumblr.com/tumblr_mam3e37V3f1qa0t06o6_250.png') !important;}




/* ======= New Post Icons ======= */


.new_post_label{
font-size:0px !important;
}

.new_post_label_icon{background-image:none !important}

#new_post{
background: url(http://25.media.tumblr.com/tumblr_mam3e37V3f1qa0t06o10_r1_1280.png) center no-repeat;
background-color:#fff !important;
}





/* ======= Selector ======= */

.selection_nipple.white,
.selection_nipple {
  border: 0px !important;
background-image:url("http://25.media.tumblr.com/tumblr_mam3e37V3f1qa0t06o2_100.png") !important;
  width:  36px !important;
  height: 48px !important;
  margin-top: -12px !important;
  background-position: 0px 0px !important;
  background-repeat: no-repeat !important;
}






/* ======= Cursor ======= */

html {cursor: url(http://25.media.tumblr.com/tumblr_mam3e37V3f1qa0t06o3_100.png), progress;}
a, a:hover {cursor: url(http://25.media.tumblr.com/tumblr_mam3e37V3f1qa0t06o4_100.png), progress;}
}
