css:
@-moz-document url-prefix("https://github.com/organizations/paperboy-petit"), url-prefix("https://github.com/paperboy-petit") {.notification-indicator.contextually-unread {
  opacity: 0.5;
}

.github-logo-4x-hover {
  opacity:0;
}

.header {
  background-image: url(http://www.petit.cc/img/common/background.jpg);
}

.header a {
  text-shadow: none;
  color: #222;
  text-shadow: 0px 0px 5px #fff;
}

.top-nav a:hover {
  -webkit-transform: rotate(720deg) scale(1.5);
  -moz-transform: rotate(720deg) scale(1.5);
}

.pagehead .container > ul.tabs {
  background-image: url(http://www.petit.cc/img/common/background.jpg);
}

.pagehead .container > ul.tabs li a {
  text-shadow: none;
  color: #fff;
  border-bottom: none;
  text-shadow: 0px 0px 5px #000;
}

.pagehead .container > ul.tabs li a:hover{
  font-weight: bold;
  color: #222;
  background: none;
  border-bottom: none;
}

.pagehead .container > ul.tabs li a .counter {
      text-shadow: 0px 0px 0px #fff;
  }

.pagehead .container > ul.tabs li a.selected,
.pagehead .container > ul.tabs li a.selected:hover {
  background-image: url(http://www.petit.cc/img/common/background.jpg);
  border-bottom: none;
  text-shadow: 0px 0px 5px #fff;
}

#wiki-wrapper #head {
  border-bottom: 10px solid #4D5687;
}

.markdown-body h1 {
  background-color: #99AED3;
  padding-left: 1px;
  border: 3px solid #4D5687;
  border-radius: 3px;
}
}
