css:
@-moz-document domain("androidbg.com") {
@font-face { font-family: Roboto; src: url('http://worldofchazz.net/roboto/RobotoCondensed-Regular.ttf'); } 
@font-face { font-family: Roboto; font-weight: bold; src: url('http://worldofchazz.net/roboto/RobotoCondensed-Bold.ttf'); }
@font-face { font-family: Roboto; font-weight: bold; font-style: italic;  src: url('http://worldofchazz.net/roboto/RobotoCondensed-BoldItalic.ttf'); }
@font-face { font-family: Roboto; font-style: italic; src: url('http://worldofchazz.net/roboto/RobotoCondensed-Italic.ttf'); }
* {
		outline:none;
		
	}
body {
	font-size: 105%;
	

	}

.brd-page {
	font: 75%/1.5em  Roboto,Verdana,Helvetica,Arial,sans-serif;
	}

.brd samp, .brd code, .brd pre, .brd option, .brd optgroup,
.brd input, .brd select, .brd textarea, .brd td, .brd th {
	font-family: Roboto,Verdana,Helvetica,Arial,sans-serif;
	}

}
