@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("nnm-club.ru") {


*{font-family: "constantia"!important;}
*{-moz-border-radius: 20px 0px 20px 0px !important;}
a{color: #000!important;
font-family: "constantia"!important;}
span{color:#000!important;}

a:hover, a:visited:hover, a:hover *, span[onclick]:hover, div[onclick]:hover, [role="link"]:hover, [role="link"]:hover *, [role="button"]:hover, [role="button"]:hover *, [role="menuitem"]:hover, [role="menuitem"]:hover *, .link:hover, .link:hover *, .sn-link:hover, .gac_b:hover *, .google-ac-b *, .yt-suggest-selected *, .yt-uix-button-menu-item:hover, .acl div.sug .sel *, .acl div.sug .sel, .ac_active, .ac_active *, .yui-ac-bd li:hover {
 color: #ff0000 !important;text-decoration: none!important}


input, select, button, a[href="javascript:;"]:not([t]):not([onclick]) {
  -moz-appearance: none !important;
  background: -moz-linear-gradient(top, #000, #444, #000) #000 no-repeat !important;
  color: #bbb !important;
  border: 2px solid #3399ff !important;
  border-radius: 4px !important}
input:hover, input[type="button"]:hover, select:hover, button:hover {
  border: 2px solid #555 !important;
  -moz-border-top-colors: #ff0000!important;
  -moz-border-bottom-colors: #ff0000!important;
  -moz-border-left-colors: #ff0000 !important;
  -moz-border-right-colors: #ff0000 !important}
input:focus, select:focus {
  box-shadow: 0 0 5px #077 !important}
input[type="checkbox"], input[type="checkbox"]:focus {
  border: 2px solid #222 !important;
  background-color: #111 !important;
  color: #DDD !important;
  border-radius: 4px !important}
input[type="radio"], input[type="radio"]:focus {
  border: 2px solid #222 !important;
  background-color: #111 !important;
  color: #BBB !important}
select input[type="button"] {border: none !important}

button:active, input[type="submit"]:active {
background: #222 !important}
input *, input, select {color: #ccc !important}



body, html, div, h1, h2, h3, h4, h5, h6, table, tbody, thead, tr, td, th, ul, li, p, i, dd, dt, strong, code, iframe, embed, section, blockquote, label, form, pre, dl, img {color: #999 !important; text-shadow: none !important; background-color: transparent !important; border-color: #444 !important; -moz-border-top-colors: rgba(0,0,0,.5) !important; -moz-border-bottom-colors: rgba(0,0,0,.5) !important; -moz-border-left-colors: rgba(0,0,0,.5)!important; -moz-border-right-colors:rgba(0,0,0,.5) !important;}


#top_adbn_div{display:none !important}
 td.row2 > table > tbody > tr > td >center {display:none !important}
 tr > td > #lx_590{display:none !important}
