css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url('https://www.tumblr.com/login'),
  url('http://www.tumblr.com/dashboard'),
  url('http://www.tumblr.com/new/blog'),
  url('http://www.tumblr.com/inbox'),
  url('http://www.tumblr.com/help'),
  url('https://www.tumblr.com/preferences'),
  url('http://www.tumblr.com/lookup'),
  url('http://www.tumblr.com/explore'),
  url('http://www.tumblr.com/following'),
  url('http://www.tumblr.com/likes'),
  url-prefix('http://www.tumblr.com/blog/'),
  url-prefix('http://www.tumblr.com/dashboard/'),
  url-prefix('http://www.tumblr.com/new/'),
  url-prefix('http://www.tumblr.com/tumblelog/'),
  url-prefix('http://www.tumblr.com/tagged/'),
  url-prefix('http://www.tumblr.com/spotlight/'),
  url-prefix('http://www.tumblr.com/edit/') , url("https://www.tumblr.com/dashboard"), url("https://www.tumblr.com/new/blog"), url("https://www.tumblr.com/inbox"), url("https://www.tumblr.com/help"), url("https://www.tumblr.com/lookup"), url("https://www.tumblr.com/explore"), url("https://www.tumblr.com/following"), url("https://www.tumblr.com/likes"), url-prefix("https://www.tumblr.com/blog/"), url-prefix("https://www.tumblr.com/dashboard/"), url-prefix("https://www.tumblr.com/new/"), url-prefix("https://www.tumblr.com/tumblelog/"), url-prefix("https://www.tumblr.com/tagged/"), url-prefix("https://www.tumblr.com/spotlight/"), url-prefix("https://www.tumblr.com/edit/") {

/*sets text color for ALL text*/
/*
* {
  color:rgba(255, 255, 255, 1.0) !important;
}
*/

/*BIG BACKGROUND begin - background for the entire screen*/
 body {
   background:url('http://www.sidleach.com/m16.jpg') no-repeat fixed !important;
   background-color: rgba(44,71,98, 0.95) !important;
   background-position: center !important;
  
 }
 /*BIG BACKGROUND end*/


#logo {height: 0 !important;
        width: 0 !important;
	padding-left: 240px !important;
	padding-top: 70px !important;
        margin-top: 6px !important; 
	background: url(http://img196.imageshack.us/img196/1285/tumblrbleu.png) no-repeat !important;}
}
/*LOGO end*/

/*ICON BAR begin - new post icons bar - graphic replacement*/
img[src*="https://secure.assets.tumblr.com/images/post_icons_sprite.png"],
.new_post_label_icon {
opacity: 0.00 !important;  
}
#new_post{
  background-image: url(http://img585.imageshack.us/img585/4760/logoooos.png) !important;
  background-position: top center !important; 
  background-repeat: no-repeat !important;
}
/*ICON BAR  end*/

/*ICON BAR BACKGROUND begin - background for the icon bar*/
new_post {
  background-color: rgba(255, 255, 255, 1) !important;
}
/*ICON BAR BACKGROUND end*/

/*AVATAR ARROW begin - arrow to the left of the icon bar*/
#new_post .arrow {
  opacity: 1.0 !important;
  margin-left: 0px !important; 
}
/*AVATAR ARROW end*/

/*CONTENT begin - background for posts, icon bar, sidebar*/
#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
  background: rgba(71,185,202,0.25) !important;
}
/*CONTENT*/ 

/*POST BACKGROUND begin*/
#posts LI[class*=" post"] {
  background: rgba(255, 255, 255, 1) !important;
}
/*POST BACKGROUND end*/

/*SELECTION ARROW begin - points to dashboard selection*/
.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) {
  background: none !important;
  width: 0px !important;
  height: 0px !important;
  margin-top: 0px !important;
  border-bottom: solid 11px rgba(255,255,255,1.0) !important;
  border-right: 11px solid transparent !important;
  border-left: 11px solid transparent !important;
}

.selection_nipple:not([class="selection_nipple white"]) {
  border-bottom: solid 11px rgba(44,71,98, 1.0)  !important;
}
/*SELECTION ARROW end*/

/*GREEN BUTTONS begin - blue/green buttons from sidebar to transparent*/
li.selected, li.recessed, a.active, div.post_filter_tabs a {
  background: none !important;
}
/*GREEN BUTTONS end*/

/*RADAR begin - removes radar blue frame - softens appearance*/
.post.photo_post .photo, .post.photo_post .video_thumbnail {
  -moz-box-shadow: 1px 1px 3px rgba(0, 0, 0, 0.0) !important;
  border-radius: 12px !important;
}
.content.is_media, .mask.top, .mask.bottom {
  background: none !important;
}
/*RADAR end*/

/*NEW CONTENT begin - text post message body background*/
.mceContentBody {
  color: rgba(0, 0, 0, 1.0) !important;
  background: rgba(255,255,255, 1.0) !important;
}
/*NEW CONTENT end*/

/*NOTIFICATION BARS TEXT begin*/
.notification.single_notification,
.notification,
.notification a,
.notification .hide_overflow a,
.notification.last_notification,
.notification.last_notification a {
  color: rgba(35,35,35,1) !important;
}
.notification.single_notification a,
.notification.first_notification a,
.notification a,
.notification.last_notification a,
.notification .hide_overflow a {
  border-bottom: solid thin rgba(64,64,64,1) !important;
}
.notification.single_notification a:hover,
.notification.first_notification a:hover,
.notification a:hover,
.notification.last_notification a:hover,
.notification .hide_overflow a:hover {
  color: rgba(128,128,128,1) !important;
  border-bottom: solid thin rgba(128,128,128,1) !important;
}
#posts > li[class^="notification"]:hover .block {
  border: none !important;
}
/*NOTIFICATION BARS TEXT end*/

/*NOTIFICATION BARS begin*/
.notification.single_notification,
.notification.first_notification,
.notification.last_notification,
.notification,
.avatar_frame,
.answer_container, 
.notes li {
  background: rgba(255,255,255, 0.55) !important;
  border-right: solid thin rgba(32,32,32, .65) !important;
  border-bottom: solid thin rgba(32,32,32, 1.0) !important;
  border-left: solid thin rgba(32,32,32, .65) !important;
}
.notification.alt {
    background: rgba(47,151,135,0.55) !important;
}
.notification.single_notification,
.notification.first_notification {
  border-top: solid thin rgba(64,64,64, .65) !important;
}
/*NOTIFICATION BARS end*/

/*NOTIFICATION BAR ARROW begin*/
.nipple {
  background: none !important;
  width: 0px !important;
  height: 0px !important;
  border-right: solid 6px rgba(255,255,255,0.5) !important;
  border-bottom: 6px solid transparent !important;
  border-left: 0px solid transparent !important;
  border-top: 6px solid transparent !important;
  margin-top: 0px !important;
  margin-left: -0px !important;
}
.nipple.border {
  background: none !important;
  width: 0px !important;
  height: 0px !important;
  border-right: solid 6px rgba(255,255,255,.75) !important;
  border-bottom: 6px solid transparent !important;
  border-left: 0px solid transparent !important;
  border-top: 6px solid transparent !important;
  margin-top: 1px !important;
  margin-left: 0px !important;
}
.alt .nipple {
  border-right: solid 6px rgba(231,188,72, .75) !important;
}
/*NOTIFICATION BAR ARROW end*/

/*FOLLOWING HEAD begin*/
.notification_type_icon.follow_icon, .controls_section .following,
.controls_section .spotlight {
  background-image: none !important;
}
.notification_type_icon.follow_icon:before, .controls_section .following:before,
.controls_section .spotlight:before {
  position: absolute !important;
  content:"" !important;
  background: rgba(255,255,255,1) !important;
  left:4px !important;
  width:14px !important;
  height: 5px !important;
  margin-top: 8px !important;
  margin-left: -4px !important;
  border-top-right-radius: 4px !important;
  border-top-left-radius: 4px !important;      
}
.notification_type_icon.follow_icon:after, .controls_section .following:after,
.controls_section .spotlight:after {
  position: absolute !important;
  content:"" !important;
  background: rgba(255,255,255,1) !important;
  left:9px !important;
  width:6px !important;
  height:10px !important;
  margin-top: 0px !important;
  margin-left: -5px !important;
  -moz-border-radius:5px 5px 5px 5px / 5px 5px 10px 10px !important;
  -webkit-border-radius:5px 5px 5px 5px / 5px 5px 10px 10px !important;
  border-radius:5px 5px 5px 5px / 5px 5px 10px 10px;
}
.controls_section .following:before,
.controls_section .spotlight:before {
  width:18px !important;
  height: 6px !important;
  margin-top: 12px !important;
  margin-left: 8px !important;
}
.controls_section .following:after,
.controls_section .spotlight:after {
  width:8px !important;
  height:12px !important;
  margin-top: -19px !important;
  margin-left: 8px !important;
}
.controls_section .spotlight .hide_overflow:before {
  position: absolute !important;
  content: "+" !important;
  color: rgba(255,255,255,1) !important;
  top: 9px !important;
  left: 22px !important;
  text-shadow: -1px 1px 1px rgba(0,0,0,1) !important;
  font: bold 20px "century gothic" !important;
}



/*REBLOG begin*/
.notification_type_icon.reblog_icon {
  margin-top: 2px !important;
  background-image: none !important;
  border-top: solid 2px rgba(64,64,64, .75) !important;
  border-right: solid 2px rgba(64,64,64, .5) !important;
  border-bottom: solid 2px rgba(64,64,64, .75) !important;
  border-left: solid 2px rgba(64,64,64,.5) !important;
  -moz-border-radius-bottomright: 1px !important;
  -moz-border-radius-topleft: 1px !important;
  -webkit-border-bottom-right-radius: 1px !important;
  -webkit-border-top-left-radius: 1px !important;
  border-bottom-right-radius: 1px !important;
  border-top-left-radius: 1px !important;
  height: 5px !important;
  width: 10px !important;
}

.notification_type_icon.reblog_icon:before {
  position: absolute !important;
  content:"" !important;
  border-top: solid 4px transparent !important;
  border-bottom: solid 4px transparent !important;
  border-left: solid 4px rgba(64,64,64, 1.0) !important;
  left: 0px !important;
  width:0px !important;
  height: 0px !important;
  margin-top: -5px !important;
  margin-left: 10px !important; 
}
.notification_type_icon.reblog_icon:after {
  position: absolute !important;
  content:"" !important;
  border-top: solid 4px transparent !important;
  border-right: solid 4px rgba(64,64,64, 1.0) !important;
  border-bottom: solid 4px transparent !important;
  left: 0px !important;
  width:0px !important;
  height: 0px !important;
  margin-top: 2px !important;
  margin-left: -4px !important;
}
#search_query,
#search_query:focus {
  font: small-caps 11px arial !important;
/*  height: 36px !important;*/
}
#search_query:focus {
  color: rgba(255,255,255,1) !important;
  background-color: rgba(255,255,255,.15) !important;
  border: solid thin rgba(255,255,255,.25) !important;
}

#return_to_top .return_to_top_icon{

background: url('http://img51.imageshack.us/img51/1584/nipple.png') no-repeat transparent!important}

.selection_nipple.white,.selection_nipple:not([class="selection_nipple white"]){background-image:url("http://img526.imageshack.us/img526/3940/arrowp.png")!important;background-repeat:no-repeat!important;width:23px!important;height:77px!important;margin-top:-12px!important}.selection_nipple.white{background-position:0 0!important;width:35px!important;height:35px!important}
.selection_nipple:not([class="selection_nipple white"]){border-bottom:solid 11px rgba(0,0,0,0)!important}#inbox_button.tab.iconic.selected 
.selection_nipple:not([class="selection_nipple white"]){margin-left:-10px!important}

@-moz-document url-prefix('http://www.tumblr.com/dashboard/iframe'), url-prefix('https://www.tumblr.com/dashboard/iframe')  {body {background:none!important;}}
