css:
@-moz-document url-prefix("https://www.tumblr.com/dashboard"), url-prefix("https://www.tumblr.com/inbox"), url-prefix("https://www.tumblr.com/settings"), url-prefix("https://www.tumblr.com/likes"), url-prefix("https://www.tumblr.com/blog"), url-prefix("https://www.tumblr.com/followers"), url-prefix("https://www.tumblr.com/reblog"), url-prefix("https://www.tumblr.com/new"), url-prefix("https://www.tumblr.com/liked"), url-prefix("https://www.tumblr.com/edit"), url-prefix("https://www.tumblr.com/tagged"), url-prefix("http://www.tumblr.com/dashboard"), url-prefix("http://www.tumblr.com/inbox"), url-prefix("http://www.tumblr.com/settings"), url-prefix("http://www.tumblr.com/likes"), url-prefix("http://www.tumblr.com/blog"), url-prefix("http://www.tumblr.com/followers"), url-prefix("http://www.tumblr.com/reblog"), url-prefix("http://www.tumblr.com/new"), url-prefix("http://www.tumblr.com/liked"), url-prefix("http://www.tumblr.com/edit"), url-prefix("http://www.tumblr.com/tagged") {

.l-content {

background:none!important;

}body.logged_in, body.logged_out {

background: url('http://i47.tinypic.com/2jb8ln7.jpg') bottom right #e6deb0 no-repeat fixed!important;
}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background-color:#f9f9f9!important;
background:rgba(105,105,105,0.3)!important;
}

#auto_pagination_loader {
background:rgba(122,186,98,0.00)!important;
}

div.return_to_top_icon {
background:none!important;
width:0!important;
height:0!important;
margin-top:0!important;
opacity:.5!important;
border-bottom:solid 30px #FFF!important;
border-right:15px solid transparent!important;
border-left:15px solid transparent!important;
}

.selection_nipple.white,.selection_nipple:not([class="selection_nipple white"]) {
background:none!important;
width:0!important;
height:0!important;
margin-top:0!important;
border-bottom:solid 11px #FFF!important;
border-right:11px solid transparent!important;
border-left:11px solid transparent!important;
}

.selection_nipple:not([class="selection_nipple white"]) {
border-bottom:solid 11px #ab9467!important;
}

.selected .following,.selected .followers,.selected .settings,.selected .posts,.selected .messages,.selected .drafts,.selected .queue,.selected .likes {
color:rgba(255,255,255,0.50)!important;
background:#B79C65!important;
}

#posts .notification.alt .nipple {
border-right:6px solid #d8bd88!important;
}

a.explore_more_tags,a.add_and_remove,a.customize,a.mass_editor {
background-color:#ab9467!important;
background-image:none!important;
border:0!important;
}

div.hide_overflow {
font-size:.9em;
}

a.tag,a.explore_more_tags,a.add_and_remove {
height:8px;
}

a.tag {
position:relative;
top:-4px;
}

.selected .tag {
height:14px;
background:#B79C65!important;
position:relative;
top:0;
color:rgba(255,255,255,0.50)!important;
}

ul {
-moz-box-shadow:1px 1px 3px #B79C65!important;
}

div.gradient,div.dashboard_nav_item,a.permalink {
background-color:#ab9467!important;
opacity:.9;
background-image:none!important;
border:0!important;
}

.post.photo_post .photo,.post.photo_post .video_thumbnail {
border-radius:16px!important;
-moz-box-shadow:1px 1px 3px #000!important;
}

blockquote {
margin-left:-5px!important;
}

ol#posts li.notification {
background-color:#dabd86!important;
border-bottom:2px solid #333!important;
color:#000!important;
}

#nav .nav_item.active,#nav .nav_item .nav_menu,#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {
background-color:#ce9e3a!important;
background-image:none!important;
border:0!important;
}

#dashboard_switch_blog_menu_current,#dashboard_switch_blog_arrow_and_divider,#dashboard_switch_blog_arrow_current {
background-image:none!important;
}

#left_column #posts .notification {
background-color:#dabd86!important;
color:#666!important;
border-color:#eee!important;
}

#posts .notification .nipple {
width:0;
height:0;
border-top:6px solid transparent;
border-bottom:6px solid transparent;
border-right:6px solid #d8bd88!important;
position:absolute;
left:-5px;
top:10px;
}

.avatar_frame {
background-color:transparent!important;
border:none!important;
}

#posts .notification a:not(.avatar_frame) {
border-bottom:none!important;
}

.content.is_media,.mask.top,.mask.bottom,.mceContentBody,li.selected,li.recessed,a.active,div.post_filter_tabs a,.content.is_media,.mask.top,.mask.bottom,div#right_column,div#right_column:after,#help_search {
background:none!important;
}

#nav .nav_item.active,#left_column #posts .post .permalink {
background-color:#ce9e3a;
}
}
