css:
@-moz-document url("https://www.tumblr.com/login"), url("http://www.tumblr.com/dashboard"), url("http://www.tumblr.com/new/blog"), url("http://www.tumblr.com/inbox"), url("http://www.tumblr.com/help"), url("https://www.tumblr.com/preferences"), url("http://www.tumblr.com/lookup"), url("http://www.tumblr.com/explore"), url("http://www.tumblr.com/following"), url("http://www.tumblr.com/likes"), url-prefix("http://www.tumblr.com/blog/"), url-prefix("http://www.tumblr.com/dashboard/"), url-prefix("http://www.tumblr.com/new/"), url-prefix("http://www.tumblr.com/tumblelog/"), url-prefix("http://www.tumblr.com/tagged/"), url-prefix("http://www.tumblr.com/spotlight/"), url-prefix("http://www.tumblr.com/edit/") , url("https://www.tumblr.com/dashboard"), url("https://www.tumblr.com/new/blog"), url("https://www.tumblr.com/inbox"), url("https://www.tumblr.com/help"), url("https://www.tumblr.com/lookup"), url("https://www.tumblr.com/explore"), url("https://www.tumblr.com/following"), url("https://www.tumblr.com/likes"), url-prefix("https://www.tumblr.com/blog/"), url-prefix("https://www.tumblr.com/dashboard/"), url-prefix("https://www.tumblr.com/new/"), url-prefix("https://www.tumblr.com/tumblelog/"), url-prefix("https://www.tumblr.com/tagged/"), url-prefix("https://www.tumblr.com/spotlight/"), url-prefix("https://www.tumblr.com/edit/") {

body {
background: url(http://i49.tinypic.com/zlr9h.jpg) #d5ebb0 center right fixed no-repeat !important;}

#logo {
  width: 0px !important;
  height: 0px !important;

  width: 196px !important;
  height: 44px !important;
}

img#content_top,img#content_bottom {
display:none;
}

img[src*="https://secure.assets.tumblr.com/images/post_icons_sprite.png"],
.new_post_label_icon {
opacity: 0.00 !important;  
}
#new_post{
  background-image: url(https://secure.assets.tumblr.com/images/post_icons_sprite.png) !important;
  background-position: top center !important; 
  background-repeat: no-repeat !important;
}



new_post {
  background-color: rgba(255, 255, 255, 1.0) !important;
}



#new_post .arrow {
  opacity: 1.0 !important;
  margin-left: 0px !important; 
}



#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
  background: #b4db71 !important;
}
 


#posts LI[class*=" post"] {
  background: rgba(255, 255, 255, 1.0) !important;
}

div.l-content #right_column .open_blog {
color: #ffffff !important;
background-color: #4f6d1c !important;
background-image: none !important;
border-radius: 6px 6px 0px 0px !important;
-webkit-border-radius: 6px 6px 0px 0px !important;
}

div.l-content #right_column .open_blog a {
overflow: hidden !important;
}

div.l-content #right_column .open_blog:hover {
background-color: #4f6d1c !important;
}
.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) {
  background: none !important;
  width: 0px !important;
  height: 0px !important;
  margin-top: 0px !important;
  border-right: 11px solid transparent !important;
  border-left: 11px solid transparent !important;
}

.selection_nipple:not([class="selection_nipple white"]) {
  border-bottom: solid 11px rgba(44,71,98, 1.0)  !important;
}



li.selected, li.recessed, a.active, div.post_filter_tabs a {
  background: none !important;
}



.post.photo_post .photo, .post.photo_post .video_thumbnail {
  -moz-box-shadow: 1px 1px 3px rgba(0, 0, 0, 0.0) !important;
  border-radius: 12px !important;
}
.content.is_media, .mask.top, .mask.bottom {
  background: none !important;
}



.mceContentBody {
  color: rgba(0, 0, 0, 1.0) !important;
  background: rgba(255,255,255, 1.0) !important;
}

#search_query,
#search_query:focus {
  font: small-caps 11px arial !important;
/*  height: 36px !important;*/
}
#search_query:focus {
  color: #4f6d1c !important;
  background-color: rgba(255,255,255,.15) !important;
  border: solid thin rgba(255,255,255,.25) !important;
}

.notification.single_notification,
.notification,
.notification a,
.notification .hide_overflow a,
.notification.last_notification,
.notification.last_notification a {
  color: rgba(64,64,64,1) !important;
}
.notification.single_notification a,
.notification.first_notification a,
.notification a,
.notification.last_notification a,
.notification .hide_overflow a {
  border-bottom: thin #92cb33 !important;
}
.notification.single_notification a:hover,
.notification.first_notification a:hover,
.notification a:hover,
.notification.last_notification a:hover,
.notification .hide_overflow a:hover {
  color: rgba(128,128,128,1) !important;
  border-bottom: thin #92cb33 !important;
}
#posts > li[class^="notification"]:hover .block {
  border: none !important;
}

.notification.single_notification,
.notification.first_notification,
.notification.last_notification,
.notification,
.avatar_frame,
.answer_container, 
.notes li {
  background: #92cb33 !important;
  border-right: solid thin #92cb33 !important;
  border-bottom: solid thin #92cb33 !important;
  border-left: solid thin #92cb33 !important;
  border-top: solid thin #92cb33 !important;
}
.notification.alt {
    background: #92cb33 !important;
}
.notification.single_notification,
.notification.first_notification {
  border-top: solid thin #92cb33 !important;
}

.nipple {
  background: none !important;
  width: 0px !important;
  height: 0px !important;
  border-right: solid 6px #92cb33 !important;
  border-bottom: 6px solid transparent !important;
  border-left: 0px solid transparent !important;
  border-top: 6px solid transparent !important;
  margin-top: 0px !important;
  margin-left: -0px !important;
}
.nipple.border {
  background: none !important;
  width: 0px !important;
  height: 0px !important;
  border-right: solid 6px #92cb33 !important;
  border-bottom: 6px solid transparent !important;
  border-left: 0px solid transparent !important;
  border-top: 6px solid transparent !important;
  margin-top: 1px !important;
  margin-left: 0px !important;
}
.alt .nipple {
  border-right: solid 6px #92cb33 !important;
}


div.l-content #tracked_tags.controls_section .tracked_tag {
box-shadow: none !important;
background-color: #4f6d1c !important;
border-top:  #658c23 1px solid !important;
border-left:  #658c23 1px solid !important;
border-bottom: #658c23 1px solid !important;
border-right: #658c23 1px solid !important;
padding-top: 0px !important;
}

div.l-content #tracked_tags.controls_section {
border: none !important;
}

div.l-content #tracked_tags a {
color: #ffffff !important;
}

div.l-content #tracked_tags.controls_section a.tag.selected {
border-top:  #658c23 1px solid !important;
border-left:  #658c23 1px solid !important;
border-bottom: #658c23 1px solid !important;
border-right: #658c23 1px solid !important;
}

div.l-content #tracked_tags.controls_section a.tag:hover {
background-color: #384e14 !important;
}

div.l-content #tracked_tags.controls_section a.tag span.count, div.l-content .tracked_tag .remove_tag, div.l-content .tracked_tag .hide_overflow {
color: #ffffff !important;
}

#logged_in #container,
#logged_in #footer {
  position: relative !important;
  right: 200px !important;
}

#content_top.content_cap,
#content_bottom.content_cap {
  display: none !important;
}
#dashboard_edit_post #container div.l-content #edit_post_controls,
#dashboard_edit_post #container div.l-content #left_column > H1 {
  margin-top: 25px !important;
} 
#dashboard_edit_post #container #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
  border-radius: 14px !important;
}

.selection_nipple.white,.selection_nipple:not([class="selection_nipple white"]) {
background:none!important;
width:0!important;
height:0!important;
margin-top:0!important;
border-bottom:solid 11px #b4db71 !important;
border-right:11px solid transparent!important;
border-left:11px solid transparent!important;
}

.selection_nipple:not([class="selection_nipple white"]) {
border-bottom:solid 11px #b4db71 !important;
}

ol#posts li.post .permalink {
background: #b4db71;
}

div.l-content #following.clearfix {
padding: 10px !important;
}


div.l-content #right_column .likes, .following, .spotlight, .posts, .followers, .drafts, .queue, .count, .link_arrow {
color: #4f6d1c !important;
}

.notification_type_icon,#right_column::after {
display:none!important;
}

}
