css:
@-moz-document domain(funnyjunk.com) {
span.roll{color:/*[[rollcolors]]*/}span.roll/*[[rolloptions]]*/
#commentsList a,#postsList a{color:#D4AF37;text-decoration:none;}#commentsList .uName,/*[[hide]]*/#postsList{color:#C0EEEE}
.greenLight{color:#FF00FF!important;}.greenTags{color:#8cca0a;}.grey{color:#8A8A8A;} .redlight {color:#FFEF00}
.greenLight{color:#FF00FF!important;}.com
.greenLight{color:#FF00FF!important;}.greenTags{color:#8cca0a;}.grey{color:#8A8A8A;} .redlight{color:#B57EDC}
#commentsList .r .thUp,#postsList .r .thUp{background:url('http://i.imgur.com/QkU8v.gif') 0 0 no-repeat;}#commentsList .r .thDn,#postsList .r .thDn{background:url('http://i.imgur.com/QkU8v.gif') -15px 0 no-repeat;} .redLight{color:#B57EDC!important;} .red{color:#B57EDC;}
#commentsList .r .thDn_i,#postsList .r .thDn_i{background:url('http://i.imgur.com/NZwsm.png') -15px 0 no-repeat!important;}
#commentsList .r .thUp_i,#postsList .r .thUp_i{background:url('http://i.imgur.com/NZwsm.png') 0 0 no-repeat!important;}
.greyLight{color:#494949;}.yellowMarker{color:#F3F4B9;}.yellow{color:#ffcb00;}.thGreen{color:#FF6103!important;}.thRed{color:#FFEF00)!important;}
.tUpBig{background:url('http://i.imgur.com/XE7fv.png') 0px 0 no-repeat;}
.tDnBig{background:url('http://i.imgur.com/XE7fv.png') -70px 0 no-repeat;}
.tDnBig_i{background:url('http://i.imgur.com/LaeUX.png') -70px 0 no-repeat;}.tUpBig_i{background:url('http://i.imgur.com/LaeUX.png') 0 0 no-repeat;} 
#alertClose #img{border:0;}#alertLink{display:block;width:100px;height:17px;background:transparent url('http://i.imgur.com/11GVp.gif') 0 0;margin-top:-1px;float:left;outline:0;}
#alertLink{display:block;width:18px;height:18px;background:transparent url('http://i.imgur.com/11GVp.gif') 0 0;margin-top:-1px;float:left;outline:0;}
#alertLink.grey{background-position:0 -18px;}
#uploadBttn>li>a{width:99px;height:30px;display:block;background:#a1a1a1 url('http://i.imgur.com/x4yQM.png') 0 0 no-repeat;}
#uploadBttn>li>a:hover,#uploadBttn.active>li>a{background:#a1a1a1 url('http://i.imgur.com/x4yQM.png') 0 -30px no-repeat;}
#uploadBttn_sub a{background:#c9c9c9 url('http://i.imgur.com/SK3jQ.png') top repeat-x;display:block;padding:8px 8px;color:#000;text-decoration:none;border:1px solid #000;margin-top:-1px;}
#uploadBttn_sub a:hover{background:#878787 url('http://i.imgur.com/Q7bFe.png') top repeat-x;font-weight:bold!important;color:#fff;}

#commentsList .com div.commentSticked{display:inline-block;height:13px;padding:0 3px 0 3px;background:url("http://i.imgur.com/hOJhc.png") no-repeat scroll right center transparent;color:#000;font-family:verdana;font-size:9px;}
#commentsList .com div.commentSticked div{background:url("http://i.imgur.com/bmKxU.png") no-repeat scroll left center transparent;height:13px;padding:0 0 0 3px;}}
