css:
@-moz-document domain(tumblr.com) {
body {

  background: url("http://fc05.deviantart.net/fs71/f/2011/184/8/7/1680_heya_by_stonem18-d3kx62l.jpg") !important; 
  background-size: 50% !important; 
}




.footer_links {
  display: display !important; 
}





#logo {
  width: 0px !important;
  height: 0px !important;
  padding-left: 196px !important; 
  padding-top: 44px !important; 
  background-image: url("http://25.media.tumblr.com/tumblr_ma4uhaFbE71r2ds6io5_250.png") !important; 
  background-repeat: no-repeat !important;
}


img[src*="http://assets.tumblr.com/images/new_post_icons_no_labels.png"]{
opacity:0.00 !important;  
}
#new_post{
  background-image: url(http://b.vimeocdn.com/ps/276/265/2762654_300.jpg) !important; 
  background-repeat: repeat !important; 
  background-size: 19% !important;
}

.post.is_mine.with_avatar.new_post {
  background: rgba(255,255,255,0.50) !important; 
}


.new_post_label{
  color: rgba(0,0,0, 1.0) !important; 
}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
  background: rgba(44,71,98, 0.50) !important; 
}


.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) {
background: none !important;
width: 0px !important;
height: 0px !important;
margin-top: 0px !important;
border-bottom: solid 11px rgba(44,71,98, 0.50) !important;
border-right: 11px solid transparent !important;
border-left: 11px solid transparent !important;
}
.selection_nipple:not([class="selection_nipple white"]) {
border-bottom: solid 11px rgba(44,71,98, 0.50)  !important;
}




.mceContentBody {
  background: url("http://imgsrc.hubblesite.org/hu/db/images/hs-1994-02-c-web.jpg") !important; 
  background-size: 100% !important; 
  color: rgba(255,255,255, 1.0) !important; 
}


#content_top {
  width: 0px !important;
  height: 0px !important;




}


#content_bottom {
  width: 0px !important;
  height: 0px !important;




}


#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
  padding-top: 25px !important; 
  padding-bottom: 25px !important; 
   -moz-border-radius: 20px !important; 
}


LABEL {
  color: rgba(0,0,0, 1.0) !important; 
}



.radar {
  display: none !important; 
}
#footer {
  display: none !important; 
}
.selected .following,
.selected .followers,
.selected .settings,
.selected .posts,
.selected .messages,
.selected .drafts,
.selected .queue,
.selected .likes  {
  color: rgba(255,255,255, 0.50) !important; 
  background: rgba(44,71,98, 1.0) !important; 
}


#new_post .arrow {
  opacity: 0.50 !important;
  margin-left: -1px !important; 
}

.controls_section li .followers:before {
  position: absolute !important;
  height: 30px !important;
  line-height: 28px !important;
  width: 92px !important;
  font: inherit !important;
}

a.followers  div.hide_overflow{
  visibility: hidden !important;
}

.controls_section li .followers:before {
  content: "Heya Lovers" !important;
}


#home_button {

    padding-right: 17px !important;
}


#home_button a {

    height: 0 !important;
    width: 0 !important;
    top: -7px !important;
    padding-left: 40px !important;
    padding-top: 45px !important;
    background: url('http://i40.tinypic.com/10g9xeb.png') !important;
background-size: 140% !important;
}


#home_button .tab_notice {
    left: 56px !important;
    right: auto !important;
}
}
