css:
@-moz-document domain("utnianos.com.ar") {
* {
	border:0 solid #CCC!important;}

body,
div.logo a:hover,
div#loginContent{
	background:#3f3f3f none 0 0 no-repeat !important;
	font-family:Ubuntu, "Droid Sans Serif",Verdana, Arial, Sans-Serif !important;}

a{
	color:white !important;}

#content,
fieldset.trow1,
fieldset.trow2{
	background:#575757!important;
	border-radius:4px !important; }

#breadcrumb_multipage_popup {
	border:0 solid #CCC!important;
	background:#575757!important;
	box-shadow:0 3px 3px rgba(0,0,0,0.2) !important; }


div > blockquote,
blockquote {
	color:white !important;
	border-top:0px  solid white !important;
	border-right:0px  solid white !important;
	border-bottom:0px  solid white !important;	
	border-left: 1px solid white !important;
	background:#3f3f3f none 0 0 no-repeat!important;
	border-radius:4px !important;}

blockquote cite{
	border-bottom:1px  solid white !important;}

dl. offtopic * {
	color: white !important;}

dl.offtopic {
	border-left: 1px solid white !important;
	border-radius:4px !important;
	background:#3f3f3f none 0 0 no-repeat!important; }	
dl.offtopic > dt{
	border-bottom: 1px solid white !important;	}


div.spoilerBox {
	border-radius:4px !important;
	background:#3f3f3f !important;
	border-left: 1px solid white !important;}
div.spoilerBox div:first-child{
	border-bottom: 1px solid white !important;
	color: white !important;}	


table.outer ul a {
	color: black !important;}

div.bottommenu,
div.codebox, 
div.offtopic, 
div.plupload_content {
	background:#3f3f3f none 0 0 no-repeat!important;
	border-radius:4px !important;}

div.pm_alert{
	background:#e5e5e5 !important;
	border-radius:4px !important;}

#pm_notice a {
	color: black !important;}

div[id*=fop_topbar] {
	background:#3f3f3f none 0 0 no-repeat !important;
	border-top-left-radius: 4px !important;
	border-top-right-radius: 4px !important;
	color: white !important;}


input[title="buscar"]{
	background:#616161 none 0 0 no-repeat!important;}	

select{
	background:#747474 none 0 0 no-repeat!important;
	border: 0px solid white!important;
	border-radius: 4px !important;}

option {
	background:#747474 none 0 0 no-repeat!important;
	border: 0px solid white!important;}

option:hover{
	background:#575757 none 0 0 no-repeat!important;
	border: 0px solid white!important;}

.popup_menu .popup_item {
	background:#4d4d4d none 0 0 no-repeat!important;}

.popup_menu .popup_item:hover {
	background:#616161 none 0 0 no-repeat!important;}



form input.textbox,
form input.button{
	border-radius:4px !important;
	background: white none 0 0 no-repeat!important;}
	

a.pagination_first,
a.pagination_previous,
a.pagination_page,
a.pagination_current,
a.pagination_last,
a.pagination_next {
	background:#575757!important; }

div.pagination span.pagination_current {
	background:#4d4d4d!important;
	color:#FFF!important;
	border-radius:4px !important;}

a.pagination_page:hover,
a.pagination_last:hover,
a.pagination_next:hover,
a.pagination_first:hover,
a.pagination_previous:hover {
	background: #4d4d4d !important;
	border-radius: 4px !important;
	color: white !important;}

div.pagination a {
	color: white !important;}

div.navigation a,
div.navigation span.active{
	color: white !important;}

div.navigation a:hover{
	background: #4d4d4d !important;
	border-radius: 4px !important;
	color: white !important;}

#panel {
	background: #575757!important;
	box-shadow: 0px 3px 3px rgba(0,0,0,0.2) !important; }

#panel a {
	text-decoration:underline !important; }
	
#panel a:hover{
	background:#575757 !important;}

#panel > img {
	box-shadow:0 3px 3px rgba(0,0,0,0.2) !important; }



div.menu ul a,
div.menu ul a:visited {
	color: white !important;
	background-image: none !important;
	background:#575757 none 0 0 no-repeat !important;
	border-top-right-radius:8px !important;
	border-top-left-radius:8px !important;
	border-bottom-right-radius:0px !important;
	border-bottom-left-radius:0px !important; }

div.menu ul a:hover{
	color:white !important;
	background:#575757 none 0 0 no-repeat !important;
	border-top-right-radius:8px !important;
	border-top-left-radius:8px !important;
	border-bottom-right-radius:0px !important;
	border-bottom-left-radius:0px !important; }
	
div.menu ul a,
div.menu ul a:visited {
	color: white !important;
	background-image: none !important;
	background:#575757 none 0 0 no-repeat !important;
	border-top-right-radius:8px !important;
	border-top-left-radius:8px !important;
	border-bottom-right-radius:0px !important;
	border-bottom-left-radius:0px !important; }

div.menu ul a:hover{
	color:white !important;
	background:#4d4d4d none 0 0 no-repeat !important;
	border-top-right-radius:8px !important;
	border-top-left-radius:8px !important;
	border-bottom-right-radius:0px !important;
	border-bottom-left-radius:0px !important; }



html body div#container div#content div#cse-search-form div.gsc-control-searchbox-only form.gsc-search-box table.gsc-search-box tbody tr td.gsc-input div#gsc-iw-id1.gsc-input-box table#gs_id0.gstl_0 tbody tr td#gs_tti0.gsib_a input#gsc-i-id1.gsc-input {
	background-color: white!important;
}

.gsc-completion-selected:hover {
	background:white none 0 0 no-repeat!important;
}

#gsc-i-id1,#gsc-iw-id1,#gs_tti0,#gs_id0, .gsib_b,div.popup_menu,.gsc-completion-container {
	background: white none 0 0 no-repeat!important;}

td.gsc-search-button{
	background: #575757 none 0 0 no-repeat!important;}

	
div#cse a{
	color:black !important;
}	
	


table.tborder {	
	border-collapse: separate !important;  }

table.tborder tbody tr {
	margin-bottom: 5px !important;}

td.thead,
td.tcat,
td.tfoot{
	background:#1a1a1a none 0 0 no-repeat !important;
	box-shadow:0 3px 3px rgba(0,0,0,0.2) !important;}

td.tcat {
	border-radius:0px !important;}

td.thead {
	border-top-left-radius:5px !important;
	border-top-right-radius:5px !important;
	border-bottom-left-radius:0px !important;
	border-bottom-right-radius:0px !important;}

tr.trow1 {
	background:#575757 none 0 0 no-repeat!important;}

td.trow1,
td.trow2 {
	border-bottom:1 solid #575757 !important;}

td.trow1,
td.trow1 a:hover
div.rowEven
fieldset.trow1{
	background:#575757 none 0 0 no-repeat!important;}

td.trow2,
td.trow2  a:hover,
div.rowOdd,
fieldset.trow2 {
	background:#747474 none 0 0 no-repeat!important;}

td.trow_sep { 
	background:#3f3f3f none 0 0 no-repeat !important;
	color: white !important;}


div#posts span.smalltext,
div#posts div.post_message[id*=pid_] {
	color: white !important; }


div#loginContent,
div#loginContent h3{
	color:white!important; }

div.plupload *{
	color: white !important;}

div.plupload_header_title,
div.plupload_header_text {
	background:#1a1a1a none 0 0 no-repeat !important;
	box-shadow:0 3px 3px rgba(0,0,0,0.2) !important;}

div.plupload_content,
div.plupload_filelist_header, 
div.plupload_filelist_footer{
	background:#575757 none 0 0 no-repeat !important;
	color:white !important;}

ul#uploader_filelist * {
	background:#747474 none 0 0 no-repeat !important;}

div.plupload_buttons a.plupload_button{
	color: #616161 !important;}

input#ajaxChatInputField{
	color: #616161 !important;}

#chatList,
#emoticonsContainer,
#bbCodeContainer{
	border-left: 1px solid white !important;
	background:#747474 none 0 0 no-repeat !important;
	border-radius:4px !important;}

#chatList div[id*=ajaxChat]{
	color: black !important;}

#ajaxChatChatListContainer div[id*=ajaxChat].rowEven,
#onlineList div[id*=ajaxChat].rowEven,
#chatList div[id*=ajaxChat].rowEven{
	background:#575757 none 0 0 no-repeat !important;}

#ajaxChatChatListContainer div[id*=ajaxChat].rowOdd,
#onlineList div[id*=ajaxChat].rowOdd,
#chatList div[id*=ajaxChat].rowOdd {
	background:#747474 none 0 0 no-repeat !important;}

#onlineList{
	border-left: 1px solid white !important;
	background:#747474 none 0 0 no-repeat !important;}

#onlineListContainer,
#onlineListContainer h3{
	color: white !important;
	background:#3f3f3f none 0 0 no-repeat !important;
	border-top-left-radius: 4px !important;
	border-top-right-radius: 4px !important;}

textarea,
#ajaxChatInputField {
	color: white !important;
	background:#3f3f3f none 0 0 no-repeat !important;	}
}
