	text-shadow:none !important;
}
.editbox {
    color:#9cc250 !important;
}
.quote {
    color:#9cc250 !important;
}
.editbox b:hover,.editbox i:hover,.editbox u:hover,.editbox span:hover,.editbox s:hover,.editbox a.link_tag:hover {
    color:#CCFF66 !important;
	text-shadow: #FFD200 0px 0px 12px !important;
}
.spoiler {
	background: #575757 !important;
	color: #ababab !important;
}
.spoiler:hover {
	color: #9cc250 !important;
}
hr {
	height:3px !important;
	border-width: 0px !important;
	background:url("data:image/png;base64,") left no-repeat !important;
}
.title {
	font-size: 1.2em !important;
	color:#9cc250 !important;
}
.omitted {
	background-color: #2d2d2d !important;
}
.thread_body {
	border:none !important;
	border-bottom: solid 1px #575757 !important;
}
.thread_header {
   	background-color:#2d2d2d !important;
   	border: solid 1px #575757 !important;
}
.thread {
	background-color:#2d2d2d !important;
	color: #bdbdbd !important;
	border: none !important;
	box-shadow:none !important;
}
.post {
	background-color:#282828 !important;
	color: #bdbdbd !important;
	border: solid 1px #575757 !important;
	box-shadow:none !important;
}
footer {
	border-top:none !important;
}
#markup table td {
	background-color:#282828 !important;
}
#markup table td.header {
	color:#bdbdbd !important;
}
.post_info {
	color: #9cc250 !important;
}
img[alt="captcha"] {
    background-color: #ffffff !important;
}
.thread_hidden {
	background-color:#282828 !important;
	border: 2px solid #222222 !important;
	box-shadow:none !important;
}
#file_span, #video_span {
	color:#9cc250 !important;
	text-shadow:none !important;
}
#file_span:hover, #video_span:hover {
	color:#CCFF66 !important;
	text-shadow: #FFD200 0px 0px 12px !important;
}
.proofmark {
background:#336666 !important;
}
.admin_proofmark {
background:#006666 !important;
}

/*input_fields and buttons*/
input[type="text"] {

	/*for firefox*/	
	-moz-appearance: none !important;

	background-color: #000000 !important;
	color: #bdbdbd !important;
	border: 2px solid #222222 !important;
}
textarea {
	-moz-appearance: none !important;	
	background-color: #000000 !important;
	color: #cccccc !important;
	border: 2px solid #222222 !important;
}
input[type="file"] {
	-moz-appearance: none !important;	
	background-color: #000000 !important;
	color: #cccccc !important;
	border:1px solid #000000 !important;
}
input[type="password"] {
	-moz-appearance: none !important;
	background-color: #000000 !important;
	border: 2px solid #222222 !important;
}
input[type="submit"] {
	-moz-appearance: none !important;	
	background-color: #3a3a3a !important;
	color: #cccccc !important;
	border: 2px solid #222222 !important;
}
input[type="checkbox"]
{
	-moz-appearance: none !important;
	background-color: #3a3a3a !important;
	color: #CC0000 !important;
	border: 2px solid #222222 !important;
}

/*new select tag button. mobile-version*/
select
{
	-moz-appearance: none !important;
	background-color: #3a3a3a !important;
	color: #cccccc !important;
	border: 2px solid #222222 !important;
}

