css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.youtube.com") {
#confirmBox, 
.share-panel-services, 
.share-panel-buttons, 
.share-panel-hangout, 
.watch-sidebar-section, 
.branded-page-v2-secondary-col  
{
display: none !important;
}

  .ad-div,
  #google_companion_ad_div,
  #watch-video-extra,
  #watch-channel-brand-div
  {
    display: none !important;
  }
  .yt-uix-expander-collapsed,
  #watch-description-clip {
    position: static !important;
  }

  #watch-video-container {
    background-color: transparent !important;
    background-image: none !important;
  }
  #watch-video {
    background-color: #171717 !important;
    background-image: -moz-linear-gradient(top,#111,#171717) !important;
    box-shadow: inset 0 0 12px;
    border-radius: 4px;
  }
  #watch-player {
    margin: 0 auto !important;
    height: 520px !important;
    width: 960px !important;
  }
  #watch-video.wide,
  #watch-video.large {
    width: 1280px !important;
  }
  #watch-video.wide #watch-player,
  #watch-video.large #watch-player {
    height: 748px !important;
    width: 1280px !important;
  }
  /*More videos from user button*/
  #watch-more-from-user{
  margin: 0 auto !important;
  width:980px !important;
  }

.branded-page-v2-col-container {
    background: none !important;
    border-left: 0px !important;
}

  #ytmp3procom{ margin-left:10px !important;}
  #content-container{ margin-top:5px !important;}
  #eow-title{color:#000000 !important;}
  #watch-headline-container{ margin-top:7px !important;}
  #watch-headline-user-info{ margin-top:10px !important;}
 
  #watch-like-unlike{ margin-left:25px !important; margin-right:25px !important;}

 #watch-sidebar {
    margin-top: 0 !important;
    padding-top: 10px !important;
  }


#page-container {
width: 1259px !important;
margin-right: auto !important;
margin-left: auto !important;
}

#yt-masthead-container, 
#footer-hh-container {
display: table !important;
width: 1259px !important;
margin-right: auto !important;
margin-left: auto !important;
}

#footer-hh-main > UL {
margin-left: 248px !important;
}

#footer-hh-links-primary {
margin-left: 211px !important;
}

#footer-hh-links-secondary {
margin-left: 323px !important;
}

.yt-picker {
margin-left: 91px !important;
margin-right: -71px !important;
}

#yt-masthead-content {
margin-left: 23px !important;
}

.site-left-aligned #alerts {
margin: 0 auto !important; 
}

.watch-medium #watch7-player {
    width: 960px !important;
    height: 540px !important;
}


.watch-wide, #watch7-sidebar {
    margin-top: 0 !important;
}
}
