css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("freeport7.org") {

html, body {
	background: url("http://imageshack.us/a/img222/4845/fp7k.jpg") center 10px fixed !important; 
	background-size: cover !important;
	background-attachment: scroll;
	color: #ffffff !important;
	opacity: 1;
}
a {
	color: #ffffff !important;
}
a:hover {
	color: #ff0000 !important;
}
header a:hover,header a.active {
	text-shadow:none !important;
}
#full_form_button,#mini_form {
	background: #000 !important;
	border:1px solid #ffffff !important;
	box-shadow:none !important;
	opacity: 0.8;
}
header {
    background: #000 !important;
    box-shadow:none !important;
    position:fixed !important;
}
#posting_info {
	color: #ff0000 !important;
}
#posting_info b {
	color: #ff0000 !important;
    background: #ffffff !important;
    border: 1px #ffffff !important;
}
.fake_link {
    color:#ff0000 !important;
}
.fake_link:hover {
	color: #ffffff !important;
}
#taglist {
background:#000 !important;
line-height:12px;
}
#full_form_button .fake_link span {
    color:#ffffff !important;
}
.editbox {
    color:#ffffff !important;
}
.quote {
    color: yellow !important;
    font-size: 12px;
}
.editbox b:hover,.editbox i:hover,.editbox u:hover,.editbox span:hover,.editbox s:hover,.editbox a.link_tag:hover {
    color: dimgrey !important;
}
.spoiler {
	background: silver!important;
	color: silver !important;
}
.spoiler:hover {
	color: #ff0000 !important;
}
section hr {
border:none !important;
border-bottom:1px solid !important;
}
.title {
	font-size: 2px !important;
	color:yellow !important;
}
.post_link {
	color:#ff0000 !important;
}
.omitted {
	background-color: #000 !important;
	color:#ff0000 !important;
}
.thread {
    text-align:center;
	background-color:#000 !important;
	color: #ffffff !important;
	display: block;
	border-radius: 5px;
	padding: 25px 25px;
	margin: auto 10px 10px ;
	opacity: 0.8;
}
.thread_hidden{
background-color: #000;
}
.post {
	background-color:#000!important;
	opacity: 0.8;
	font: 12px Trebuchet MS;
	color: #ffffff!important;
	border-radius: 5px;
	border: solid 1px #ffffff !important;
	box-shadow:none !important;
}
footer {
	border-top:none !important;
}
.pages a.current {
	background:#000 !important;
}
#markup table td {
	background-color:#282828 !important;
}
#markup table td.header {
	color:#bdbdbd !important;
}
.post_info {
	color: #9cc250 !important;
}
}
