css:
@-moz-document url-prefix('http://www.tumblr.com'), url-prefix('https://www.tumblr.com')   {

#dashboard_create_tumblelog, #dashboard_directory, #dashboard_lookup_contacts, #dashboard_likes, #dashboard_transcoding, #main_help, #dashboard_tumblelog_settings, #account_display_onboard, #dashboard_block, #main_doc_page, #dashboard_settings, #dashboard_index, body#dashboard_edit_post, #dashboard_drafts, #dashboard_submissions, #dashboard_post_queue, #dashboard_followers, #dashboard_following, #tagged_display, #dashboard_inbox {
background: #CD0000 url('http://i47.tinypic.com/14tsx9c.png') bottom left fixed no-repeat !important;
}

#return_to_top .return_to_top_icon {
background: url('http://i50.tinypic.com/2cx95ky.png') !important;
}

body {

background: #FF0000  url('http://i49.tinypic.com/25zgnx0.png') right bottom no-repeat !important;

background-attachment: fixed !important;

}



#logo {height: 0 !important;

	width: 0 !important;

	padding-left: 200px !important;

	padding-top: 71px !important;

	background: url(http://oi48.tinypic.com/ncxfmq.jpg) no-repeat !important;}



ol#posts li.notification {



  background-color: #FF5151   !important;



  border-top: 3px solid #FF2323 !important;



  border-left: 3px solid #FF2323 !important;



  border-bottom: 3px solid #FF2323 !important;



  border-right: 3px solid #FF2323 !important;



  color: #FF0000 !important;



  text-decoration: none !important;



}






#posts .notification a:not(.avatar_frame) {



color: rgba(00, 00, 00, 1) !important;



font-weight: bold;



text-decoration: none !important;



border-bottom: 0px solid #FF2323 !important;



-webkit-background-clip: padding-box;



}







#posts .notification blockquote {



  border-left: solid 3px #FF2323 !important;



}







#posts .notification .avatar_frame {



background-color: #FF4949 !important;



border: 1px solid #FF2323 !important;



position: absolute;



top: -1px;



left: -55px;



overflow: hidden;



display: inline-block;



}

























.post.is_mine.with_avatar.new_post {



  color: #ffffff !important;



  font-family:courier, monospace !important;



  background: rgba(0,0,0,0.50) !important; 



}









img[src*="http://assets.tumblr.com/images/new_post_icons_no_labels.png"]{



opacity:0.00 !important;  



}





#new_post{



  background-image: url !important; 



  background-repeat: no-repeat !important; 



}





#posts .post.new_post .new_post_label {



-webkit-font-smoothing: antialiased;



opacity: .9;



font-size: 14px;



font-weight: normal;



color: #ffffff;



text-decoration: none;



text-transform: uppercase;



text-align: center;



width: 74px;



position: absolute;



}







#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {



  background: rgba(246, 0, 0, .00) !important; 



}















.selection_nipple:not([class="selection_nipple white"]) {



border-bottom: solid 11px rgba(00,00,00, 0.50)  !important;



}







#content_top {



  width: 0px !important;



  height: 0px !important;









}



.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) {



background: none !important;



width: 0px !important;



height: 0px !important;



margin-top: 0px !important;



border-bottom: solid 11px rgba(00,00,00, 0.50) !important;



border-right: 11px solid transparent !important;



border-left: 11px solid transparent !important;



}









#content_bottom {



  width: 0px !important;



  height: 0px !important;



















}











#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {



  padding-top: 25px !important; 



  padding-bottom: 25px !important; 


}











LABEL {



  color: rgba(0,0,0, 1.0) !important; 



}









#footer {



  display: none !important; 



}







.radar {



  display: none !important; 



}





.selected .following,



.selected .followers,



.selected .settings,



.selected .posts,



.selected .messages,



.selected .drafts,



.selected .queue,



.selected .likes  {



  color: rgba(0, 0, 0, 0.50) !important; 



  background: rgba(0, 0, 0, 0.0) !important; 



}











#new_post .arrow {



  opacity: 0.50 !important;



  margin-left: -1px !important; 



}

}
