css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("homebrewtalk.com") {

html,body{background:#000!important;}

a{color:#fd9!important;}

.main-content,.index-boxleft,.index-boxmiddle,.index-boxright,.index-topright *{background:#002!important;border-color:#00aa!important;}

/*spam die*/
td[width="62%"],#abcd{display:none!important;}

/*top of page stuff*/
div[align="left"]{background:#000!important;}
.vbmenu_control,.vbmenu_control b{background:#115!important;color:#fd9!important;border-color:#229!important;}

.thead{background:#006!important;}
.tcat{background:#003!important;}

/*standard alts*/
.alt1,.alt1Active{background:#111!important;color:#fff!important;}
.alt2,.alt2Active{background:#222!important;color:#fff!important;}

/*posting*/
.panelsurround{background:#002!important;}
.panel{background:#001!important;border-color:#005!important;}
.vBulletin_editor{background:#000!important;border-color:#005!important;}

table{background:transparent!important;}
}
