css:
/* AGENT_SHEET */
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);
@-moz-document url("chrome://browser/content/bookmarks/bookmarksPanel.xul"), 
url("chrome://browser/content/browser.xul"),
url("chrome://browser/content/history/history-panel.xul") {

#sidebar-box {
position:fixed!important;
top:88px;
bottom:0px;
width: 0px !important;
overflow-x: hidden !important;
-moz-appearance: none !important;
background: rgb(76,76,76)!important; 
  background: -moz-linear-gradient(-45deg, rgba(76,76,76,1) 0%, rgba(89,89,89,1) 12%, rgba(102,102,102,1) 25%, rgba(71,71,71,1) 39%, rgba(44,44,44,1) 50%, rgba(0,0,0,1) 51%, rgba(17,17,17,1) 60%, rgba(43,43,43,1) 76%, rgba(28,28,28,1) 91%, rgba(19,19,19,1) 100%)!important;
border: 1px solid #222 !important;
border-left: 0 !important;
border-radius: 0 6px 6px 0 !important;
opacity: 0!important;
-moz-transition: width 1s ease-out 1s, opacity 1s ease-out 1s !important;
}

#sidebar-box:hover {
width: 240px !important; 
opacity: .95 !important;
-moz-transition: width .2s ease-out .5s, opacity .2s ease-out .5s !important;
}

#sidebar-splitter{
display:none !important;}

#sidebar-header,#bookmarks-view-children,#sidebar-search-container {
width:100%;
background-color:transparent !important;
-moz-appearance:none!important; 
border-color:transparent !important;
color: #fff !important;
}

#sidebar {
width:100%!important;
height:92%;
background-color:transparent !important;
}

#search-box {
-moz-appearance: none !important;
background: transparent !important;
border: 0px !important;
height: 20px !important;
color: #fff !important;
}

#sidebar-search-label {
color: #fff !important;
}

#bookmarksPanel,
 #bookmarks-view, 
#history-panel,
 #historyTree {
-moz-appearance: none !important;
background-color:transparent!important;}

#history-panel #viewButton {
-moz-appearance: toolbarbutton !important;
color: #fff !important}



#bookmarksPanel tooltip, 
#history-panel tooltip {
-moz-appearance: none !important;
background: rgb(76,76,76)!important; 
  background: -moz-linear-gradient(-45deg, rgba(76,76,76,1) 0%, rgba(89,89,89,1) 12%, rgba(102,102,102,1) 25%, rgba(71,71,71,1) 39%, rgba(44,44,44,1) 50%, rgba(0,0,0,1) 51%, rgba(17,17,17,1) 60%, rgba(43,43,43,1) 76%, rgba(28,28,28,1) 91%, rgba(19,19,19,1) 100%) !important;
border: 1px solid #222 !important;
color:  #fff !important;
border-radius: 6px !important;
}

.sidebar-placesTreechildren::-moz-tree-separator{
border: 1px solid #555 !important;  
border-width: 0 0 1px 0 !important;}

.sidebar-placesTreechildren::-moz-tree-row {
outline: none !important;
border: 0 !important;}


.sidebar-placesTreechildren::-moz-tree-row(hover){ background: linear-gradient(to left, rgba(0,0,0,.25), black)!important; 
border-radius: 0px !important;}

.sidebar-placesTreechildren::-moz-tree-row(selected) {
border-radius: 0px !important;
border: 0 !important;
background: -moz-linear-gradient(left, rgba(0,0,0,0.65) 0%, rgba(0,0,0,0) 100%)!important;}

.sidebar-placesTreechildren::-moz-tree-cell-text(container) {
color: #fff !important;
font-weight: bold !important;}



.sidebar-placesTreechildren::-moz-tree-cell-text(container, open) {
color: #fff !important;}

.sidebar-placesTreechildren::-moz-tree-cell-text(container, open, selected) {
color: #fff !important;
}

.sidebar-placesTreechildren::-moz-tree-cell-text(leaf) {
color: #fff !important; 
}

.sidebar-placesTreechildren::-moz-tree-cell-text(leaf, hover)  {
color: #fff !important;
}

/*--Scrollbars by makondo--*/

scrollbar * { 
-moz-appearance: none !important;
background: transparent !important; }

scrollbar { 
-moz-appearance: none !important;
background: transparent !important;
border: none !important; 
}

scrollcorner { 
-moz-appearance: none !important;
background: transparent !important; 
width: 0 !important;
height: 0 !important; 
}

scrollbar thumb { 
-moz-appearance: none !important; 
min-height: 50px !important;
width: 0 !important;
border: none !important;
background:  transparent url("data:image/gif;base64,") center no-repeat !important; 
opacity: .1 !important; 
}

scrollbar[orient="vertical"] > scrollbarbutton[type="decrement"] {
margin-left: 0px !important; 
margin-right: 2px !important; 
}

scrollbar[orient="vertical"] > scrollbarbutton[type="increment"] { 
margin-right: 1px !important; 
}

scrollbarbutton {
border: none !important;
background: transparent url(data:image/png;base64,) center no-repeat !important;
opacity: .3 !important; 
}
}
