css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("chuza.gl") {

    .comments .comments-list{
        padding-left:5px !important;
    }

    .comments .comments-list>div,
    .comments .cmt{
        position:relative;
    }
    .comments .comments-list>div>div:first-child,
    .comments .cmt>div:first-child{
        position:absolute;
        left:-20px;
    }

    .comment-body{
        padding:10px !important;

        background-color: #98cb65 !important;
        border:1px dotted #689b35 !important;	   
        border-radius: 0 !important;
        -moz-border-radius: 0 !important;
        -webkit-border-radius: 0 !important;
    }

    .comments .comment-body,
    .comments .comment-body-hidden{
        position: relative !important;        
        overflow:visible !important;
        margin-bottom:40px !important;
    }
    .comments .comment-body-hidden{
        padding:10px !important;
    }
    .comments .comment_mc{
        position: absolute !important;
        top: 100% !important;
        width:100% !important;
        margin: 0 0 0 -11px !important;
    }
        .comments .comment-votes-info{
            float:left !important;
        }
        .comments .comment-info{
            float:right !important;
            margin-right: -2px !important;
        }


    #wrap #header:after{ /*porco*/
        content:"";
        background: url("data:image/png;base64,") no-repeat transparent;
        width:36px;
        height:36px;
        float:left;
    }

}
