css:
    @namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");
    @media all and (-moz-windows-compositor) {

#TabsToolbar .arrowscrollbox-scrollbox {
         padding: 0px !important;}

#TabsToolbar .tabbrowser-tab:before {
         display: none !important;}

#main-window #TabsToolbar:after {
         box-shadow: 0px 0px 90px 5px rgb(0,0,0) !important;
         margin: 0px 5px !important;}

#TabsToolbar .tab-background {
         margin: 0px 1px !important;
         background: transparent !important;
         box-shadow: inset 0px 0px 60px 0px #e3e3e3 !important;
         border-radius: 2px 2px 0px 0px !important;
         opacity: .1 !important;}

#TabsToolbar .tab-background-start,
#TabsToolbar .tab-background-end {
         display: none !important;}

#TabsToolbar .tab-background-middle {
         margin: -4px -1px !important;
         background: transparent !important;}

#TabsToolbar .tabbrowser-tab:after,
#TabsToolbar .tabbrowser-tab:before{
         display: none !important;}

#TabsToolbar .tabs-newtab-button {
         width: auto !important;
         margin: 0px 0px 0px 1px!important;
         background: none !important;
         padding: 0 1px !important;}

#TabsToolbar .tabbrowser-tabs .tabs-newtab-button > .toolbarbutton-icon {
         margin: 0x 0px !important;
         padding: 5px 5px !important;}
             .tabbrowser-tabs .tabs-newtab-button:not([disabled=true]):hover > .toolbarbutton-icon {
         background: linear-gradient(to bottom, hsla(198, 30%, 95%, .45), hsla(198, 10%, 80%, .35), hsla(198, 20%, 50%, .2), hsla(198, 20%, 100%, 0)),
                    -moz-radial-gradient(center 5px, circle cover, hsla(0, 0%, 100%, .4), hsla(0, 0%, 50%, 0) 100%),
                    -moz-linear-gradient(hsla(0, 0%, 45%, .2), hsla(0, 0%, 88%, .1) 50%);}

#TabsToolbar .tabbrowser-tab, .tabs-newtab-button {
    /*   margin: 0px 0px !important;
         padding: 0px 0px !important;  */
         background: linear-gradient(to bottom, hsla(0, 0%, 100%, .25) 2px, hsla(0, 0%, 100%, .1), hsla(0, 0%, 100%, .11), hsla(0, 0%, 0%, 0));
 	 color: #ffffff !important;
	 text-shadow: #000000 0px 0px 2px !important;
         border-style: none !important;
         border-radius: 2px 2px 0px 0px !important;
         border-color: transparent !important;}

#TabsToolbar .tabbrowser-tab[selected]{
    /*   margin: 0px 0px !important;
         padding: 0px 0px !important;  */
         background: linear-gradient(to bottom, hsla(198, 30%, 95%, .9), hsla(198, 10%, 80%, .75), hsla(198, 20%, 50%, .4), hsla(198, 20%, 100%, 0));
         border-radius: 2px 2px 0px 0px !important;
         background-clip: padding-box !important;}

#TabsToolbar .tabbrowser-tab:hover {
    /*   margin: 0px 0px !important;
         padding: 0px 0px !important;  */
         background: linear-gradient(to bottom, hsla(198, 30%, 95%, .45), hsla(198, 10%, 80%, .35), hsla(198, 20%, 50%, .2), hsla(198, 20%, 100%, 0)),
                    -moz-radial-gradient(center 5px, circle cover, hsla(0, 0%, 100%, .4), hsla(0, 0%, 50%, 0) 100%),
                    -moz-linear-gradient(hsla(0, 0%, 45%, .2), hsla(0, 0%, 88%, .1) 50%);
 	 color: #ffffff !important;
	 text-shadow: #000000 0px 0px 2px !important;
         border-style: none !important;
         border-radius: 2px 2px 0px 0px !important;
         border-color: transparent !important;}

#TabsToolbar {
	 background: transparent !important;
         margin-top: -2px !important;
         margin-bottom: -2px !important;
 	 color: #ffffff !important;
         border-style: none !important;
         border-color: transparent !important;}

#nav-bar {
         background: none !important;
         margin-top: 0px !important;
         margin-bottom: 0px !important;
	 color: #FFFFFF !important;
         box-shadow: none !important;
         border-style: none !important;
         border-color: transparent !important;}

#PersonalToolbar {
         background: none !important;
         margin-top: -6px !important;
         margin-bottom: 2px !important;
	 color: #FFFFFF !important;
         box-shadow: none !important;
         border-style: none !important;
         border-color: transparent !important;}

#urlbar, #searchbar textbox {
         margin-top: 0px !important;
         margin-bottom: 0px !important;
         background: transparent !important;
         box-shadow: inset 0px 0px 1px 0px #e3e3e3 !important;
         border-radius: 3px 3px 3px 3px !important;
         color: #ffffff !important;}

#personal-bookmarks .toolbarbutton-text {
         color: #ffffff !important;
         text-shadow: #000000 0px 0px 2px !important;}

#PanelUI-button {
         background-image: none !important;}

#main-window[inFullscreen="true"]{
         -moz-appearance: -moz-win-borderless-glass !important;
         background: none !important;}

#navigator-toolbox :-moz-any(#back-button, #forward-button, #home-button, #print-button, #downloads-button, #bookmarks-menu-button, #new-tab-button, #new-window-button, #fullscreen-button, #sync-button, #feed-button, #tabview-button, #webrtc-status-button, #social-share-button, #open-file-button, #find-button, #developer-button, #preferences-button, #privatebrowsing-button, #save-page-button, #switch-to-metro-button, #add-ons-button, #history-panelmenu, #nav-bar-overflow-button, #PanelUI-menu-button, #characterencoding-button, #email-link-button, #sidebar-button, #zoom-out-button, #zoom-reset-button, #zoom-in-button, #cut-button, #copy-button, #paste-button) > .toolbarbutton-icon {
	 list-style-image: url("chrome://browser/skin/Toolbar-inverted.png") !important;}

#main-window #alltabs-button {
         list-style-image: url("chrome://browser/skin/mainwindow-dropdown-arrow-inverted.png") !important;}

#main-window #tabview-button {
	 list-style-image: url("chrome://browser/skin/tabview/tabview-inverted.png") !important;}

#main-window .tabs-newtab-button, #main-window #TabsToolbar #new-tab-button > .toolbarbutton-icon {
	 list-style-image: url("chrome://browser/skin/tabbrowser/newtab-inverted.png") !important;}

#main-window .scrollbutton-up, #main-window .scrollbutton-down {
	 list-style-image: url("chrome://browser/skin/tabbrowser/tab-arrow-left-inverted.png") !important;}}
