css:
@-moz-document url-prefix("http://www.tumblr.com/"), url-prefix('https://www.tumblr.com/')   {
.new_post_label{
font-size:0px !important;
}

.new_post_label_icon{background-image:none !important}

.new_post_label i.icon_post_text {
    color: transparent !important;
}

.new_post_label i.icon_post_photo {
    color: transparent !important;
}

.new_post_label i.icon_post_quote {
    color: transparent !important;
}

.new_post_label i.icon_post_link {
    color: transparent !important;
}

.new_post_label i.icon_post_chat {
    color: transparent !important;
}

.new_post_label i.icon_post_audio {
    color: transparent !important;
}

.new_post_label i.icon_post_video {
    color: transparent !important;
}

/* Code repaired by admin. See http://forum.userstyles.org/discussion/37591/attempting-to-mass-fix-broken-tumblr-dash-icon-styles */
#new_post{
background: url('https://lh4.googleusercontent.com/-PKwJ4DfcI8o/Uw_-KPvsxiI/AAAAAAAAARw/us24ltekYLE/s513/cf%2521%2521v_tumblr_icons_v1r.png') center no-repeat !important;
background-color:#fff !important;
}
img[src*="http://assets.tumblr.com/images/new_post_icons_no_labels.png"]{
    width:0;
    height:70px;
    padding-right:250px;
    opacity: 0;
}
}

@-moz-document url-prefix("http://www.tumblr.com/"), url-prefix('https://www.tumblr.com/')   {
#home_button {

    padding-right: 20px !important;
}


#home_button a {

    height: 0 !important;
    width: 0 !important;
    top: 0px !important;
    padding-left: 37px !important;
    padding-top: 38px !important;
    background: url('https://lh4.googleusercontent.com/-MkgjkSlnNnc/Uw_-KOUMZqI/AAAAAAAAARs/xkxJcn85NDQ/s37/Vanguard%2520Symbol.png') !important;
}

#home_button .tab_notice {
    left: 56px !important;
    right: auto !important;
}
}
