css:
@-moz-document domain('play.google.com') {

.results-section,
.play-bar-container,
.play-bar,
.menu-bar-container,
.padded-content2,
.content-cell,
.padded-content5,
.padded-browse-carousel,
.padded-content3,
.attribution .badges-badge-icon-annotation-developer,
body { background: #1d1d1d; }

.menu-bar-left,
.menu-bar-right { border-color:#000; }
.menu-bar-wrapper { background:#333; border:1px solid #000; }

.menu-bar .menu-bar-button .menu-bar-button-link,
.menu-bar .menu-bar-button.highlight a { color:#ccc; }

.menu-bar-container,
.play-bar-container {
border:none;
}

.category-item a, .category-subitem a, .category-item a:visited, .category-subitem a:visited,
.details-page {
color:#aaa;
}

a, a:visited { color:#999; }

.results-section,
h3, h3 a, h3 a:visited {
color:#08C !important,;
}

.snippet a.title,
.snippet-tall .details a.title  {
color:#08C;
}


.device-compatible-container,
#write-review-toggle { border:1px solid #000; }

.doc-review-rating-option {
background-color:#1d1d1d;
}

.doc-credits hr, .doc-reviews-page hr, .doc-review-summary hr,
.buy-button-price,
.uninstall-icon-wrapper,
.device-compatible-container  {
background-color:#111 !important;
}

.layout-table .stripes { background-color:#333; }

.doc-metadata-extra h3, .doc-similar h3,
.doc-description-show-all,
.buy-button-price,
.uninstall-icon-wrapper,
.doc-metadata { border-color:#111 !important; }

#write-review-toggle,
.doc-description-show-all-button,
.average-rating-panel { background:#111 !important; }

.doc-description-show-all-button {
border-left: 5px solid #111;
}

.doc-description-collapsed .doc-description-overflow {
background-image: -webkit-gradient(linear,left top,left bottom,from(rgba(29, 29, 29, 0)),to(rgba(29, 29, 29, 1.0)));
background-image: -moz-linear-gradient(top center,rgba(29, 29, 29, 0) 20%,rgba(29, 29, 29, 1.0) 95%);
}

.screenshot-carousel-left-fade,
.screenshot-carousel-right-fade {
display:none;
}

.review-controls { margin-top:10px; background:#333; }

.stripes { background:none; }

.play-bar #search-text { background-color:#111; border-color:#000; color:#999; }

}
