/* ==UserStyle==
// @name	LR surfthechannel show/episode title listings redo
// @author	Len Rodman
// @version	1.1
// ==/UserStyle==*/

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document 
url-prefix(http://www.surfthechannel.com/show/),
url-prefix(http://www.surfthechannel.com/cat/) {

.listings:not(:hover) {		/*counteracts hover re-flow*/
	margin-top:16px !important;}

  /*h2.f-norm{			/*headings with less below
	margin-bottom:-12px !important;}*/

.listings>li {
	border-bottom-style: none !important;}

.listings>li:not(:hover) {
	margin: -6px 3px -4px 3px !important;}

.listings>li:hover {
	margin:0px 3px 1px 3px !important;
	padding:0px 3px 0px 3px !important;
	font-weight: bold !important;
	outline: 3px solid !important;
	outline-color: rgba(0,0,166,.6) !important;
	-moz-outline-radius: 3px !important;}

UL:hover > LI:first-child {
	margin-top:7px !important;}	/*fixes hover reflow*/

UL > LI:first-child:hover {
	margin-top:9px !important;}	/*top entry reflow*/

UL > LI:last-child:hover {
	margin-bottom:-3px !important;}	/*bottom entry reflow*/

  
  /*#left > H2:first-child + UL:not(:hover) > LI:first-child {
	margin-top:-12px !important;}	/*less margin below heading

    #left > H2:first-child + UL:hover > LI:first-child {
	margin-top:-2px !important;}	/*fixes hover reflow*/


}/*end @-moz-document domain("www.surfthechannel.com")*/



@-moz-document 
url-prefix("http://www.surfthechannel.com/info/television/") {

EMBED[height="360"][align="middle"][width="590"][name="flvplayer"][src="/player/flvplayer_elite.swf"][type="application/x-shockwave-flash"][splayername="SWF"][tplayername="SWF"], .screen {
	height: 432px !important;
	width: 708px !important; }

.screen {
	margin-top: -16px !important; }

div.info {
	margin-left: 106px !important;
	width: 240px !important; }

.menu-container, .header, .content-bg, .footer, .footbath {
	margin-left: -8px !important;
	margin-right: 30px !important; }


}/*end ("http://www.surfthechannel.com/info/television/")*/
