@-moz-document url-prefix("https://www.google.com/calendar/"), url-prefix("https://calendar.google.com/") {
/* Google Calendar / Highlight Calendar (Orange) */
/* author: nukumori.org */
/* last updated: 2018-03-31 */
/* -------------------------------------------------------------------------------- */
/* for old look before 2017-2018 */
/* last updated: 2013-02-27 */
/* - orange = most top-right of palette: #ffad46 */
/* === month */
div[style*="border:1px solid #FFAD46"],
div[style*="border:1px solid #CB7403"]
{
  border-radius:.65em;
}
div[style*="color:#cf801"].te,
div[style*="color:#d38c2"].te,
div[style*="color:#d48c3"].te,
div[style*="color:#d48d3"].te,
div[style*="color:#d48e3"].te,
div[style*="color:#d48f3"].te,
div[style*="color:#d58f3"].te,
div[style*="color:#d5903"].te,
div[style*="color:#d5913"].te,
div[style*="color:#dfab67"].te,
div[style*="color:#CB7403"].te,
div[style*="color:#cc7708"].te,
div[style*="color:#cd7a0e"].te,
div[style*="color:#ce7d14"].te,
div[style*="color:#d0831f"].te,
div[style*="color:#d18625"].te,
div[style*="color:#d38a2b"].te,
div[style*="color:#d38a2c"].te,
div[style*="color:#d38b2d"].te,
div[style*="color:#d38b2e"].te,
div[style*="color:#d38c2e"].te
{
  border-bottom:1px solid;
}

/* === day/week/7days */
dl[style*="background-color:#ffb75"],
dl[style*="background-color:#ffc47"],
dl[style*="background-color:#ffba6"],
dl[style*="background-color:#ffbd6"],
dl[style*="background-color:#ffc17"],
dl[style*="background-color:#ffc57"],
dl[style*="background-color:#ffc67"],
dl[style*="background-color:#ffc68"],
dl[style*="background-color:#ffc78"],
dl[style*="background-color:#ffc88"],
dl[style*="background-color:#ffc98"],
dl[style*="background-color:#ffca8"],
dl[style*="background-color:#ffcb8"],
dl[style*="background-color:#ffcc8"],
dl[style*="background-color:#ffcd8"],
dl[style*="border-color:#FFAD46"],
dl[style*="border-color:#CB7403"]
{
  border-radius:.65em;
}
dl[style*="background-color:#ffb75"]>div>.mask,
dl[style*="background-color:#ffc47"]>div>.mask,
dl[style*="background-color:#ffba6"]>div>.mask,
dl[style*="background-color:#ffbd6"]>div>.mask,
dl[style*="background-color:#ffc17"]>div>.mask,
dl[style*="background-color:#ffc57"]>div>.mask,
dl[style*="background-color:#ffc67"]>div>.mask,
dl[style*="background-color:#ffc68"]>div>.mask,
dl[style*="background-color:#ffc78"]>div>.mask,
dl[style*="background-color:#ffc88"]>div>.mask,
dl[style*="background-color:#ffc98"]>div>.mask,
dl[style*="background-color:#ffca8"]>div>.mask,
dl[style*="background-color:#ffcb8"]>div>.mask,
dl[style*="background-color:#ffcc8"]>div>.mask,
dl[style*="background-color:#ffcd8"]>div>.mask,
dl[style*="border-color:#FFAD46"]>div>.mask,
dl[style*="border-color:#CB7403"]>div>.mask
{
  display:none;
}
div.chip{
  background:#fff;
}

/* === agenda */
.lv-event-title[style="color:#CB7403"]
{
  text-decoration:underline !important;
}
/* -------------------------------------------------------------------------------- */
/* totally re-written for new looking calendar after 2017-2018 */
/* changes mango colored items to make easy to be finded out */
/* last updated: 2018-03-31 */
/* - make texts underlined & solid corner */
/* ## future events */
/* ### month */
div[style*="border-color:#F09300;"] .g3dbUc,
div[style*="border-color: rgb(240, 147, 0);"] .g3dbUc,
/* ### week */
div[style*="border-color: #F09300;"] .FAxxKc,
div[style*="border-color: rgb(240, 147, 0);"] .FAxxKc,
/* ## past events */
/* ### month */
div[style*="border-color:#f8c980;"] .g3dbUc,
div[style*="border-color: rgb(248, 201, 128);"] .g3dbUc,
/* ### week */
div[style*="border-color: #f8c980;"] .FAxxKc,
div[style*="border-color: rgb(248, 201, 128);"] .FAxxKc {
  -webkit-text-decoration-line: underline;
  text-decoration-line: underline;
  border-radius: 0;
}
/* ## circle bullets */
/* - make bullets rectangle */
/* ### color selector */
[aria-label="Mango, set calendar color"],
/* ### past week events */
.EfQccc.Hrn1mc.EiZ8Dd[style*="border-color: #f8c980;"]::after,
.EfQccc.Hrn1mc.EiZ8Dd[style*="border-color: rgb(248, 201, 128);"]::after,
.EfQccc[style*="border-color: #f8c980;"],
.EfQccc[style*="border-color: rgb(248, 201, 128);"],
/* ### future week events */
.EfQccc.Hrn1mc.EiZ8Dd[style*="border-color: #F09300;"]::after,
.EfQccc[style*="border-color: #F09300;"],
/* ### list */
.Oxm52e[style*="border-color: #F09300;"],
.Oxm52e[style*="border-color: rgb(240, 147, 0);"],
.Oxm52e[style*="border-color: #fbdfb3;"],
.Oxm52e[style*="border-color: rgb(251, 223, 179);"],
/* ### future & past events with time on calendar */
div[style*="border-color:#F09300;"] .VrDepf,
div[style*="border-color:#f8c980;"] .VrDepf {
  border-radius: 0;
}
/* -------------------------------------------------------------------------------- */
}
