css:
@-moz-document url-prefix("https://play.google.com/music/listen") {
#gbx3,
#gbx1,
#gbz,
#gbu,
#gbq1,
#gbqfb,
#gbwa,
div.gb_ba,
a.gb_q,
a.gb_y,
div.gb_na,
div.gb_Ra,
div.gb_Ua,
div.gb_ea,
a.gb_A,
div.gb_6,
div.gb_6a,
div.gb_7,
div.gb_8 {
  display: none !important;
}

#gb,
#gbq,
#oneGoogleWrapper,
div.gb_qb,
div.gb_f,
div.gb_cb,
div.gb_Ta,
div.gb_j,
div.gb_Wa,
div.gb_k,
div.gb_e,
div.gb_Cb,
div.gb_Ub,
div.gb_Vb,
div.gb_3b,
div.gb_4b,
div.gb_n,
div.gb_o,
div.gb_i,
div.gb_r {
  height: 0px !important;
  top: 0px !important;
  width: 0px !important
}

div.gb_ab {
  height: 0px !important;
}

#content {
  top: 32px !important;
}

div.queue-details,
div.queue-details .images {
  height: 100px !important;
}

div.queue-details .info {
  right: 0 !important;
  padding: 10px 0 0 20px !important;
  height: 80px !important;
}

div.queue-details .fade-out.black:after {
  background: -webkit-linear-gradient(right,rgba(255,0,0,0),rgba(255,0,0,1)) !important;
  background: linear-gradient(to right,rgba(0,0,0,0),rgba(0,0,0,0)) !important;    
}

#content.has-queue-details #main {
  top: 100px !important;
}

#content-container.has-queue-details #music-content {
  top: 133px !important;
}

#music-content {
  top: 32px !important; 
}

#gbqfw {
  width: 120px !important;
  position: absolute !important;
  top: 9px !important;
}

#gbq2 {
  margin-top: -6px !important;
  margin-left: 39px !important;
  width: 0px !important;
}

#gbqf {
  width: 118px !important;
  max-width: 118px !important;
  min-width: 118px !important;
}

.gbqfqw {
  border-radius: 15px !important;
  height: 25px !important;
}

.music-banner {
  height: 32px !important;
  }
.music-banner-icon {
    margin: 0 0 0 3px !important;
}

.music-banner-title-container {
  display: none !important;
}

#headerBar {
  height: 32px !important;
  border-bottom: 0px !important;
}

.nav-bar {
  padding: 0 30px 0 26px !important;
  background-color: #fb8521 !important;  
}

#action_bar_container .button {
  margin: 3px 5px 3px 0px  !important;
  padding: 0 7px !important;
}

#action_bar_container [data-id="upload-music"],
#action_bar_container [data-id="shuffle-my-library"],
#action_bar_container [data-id="im-feeling-lucky"]{
  display: none !important;
}

#butter_bar {
  height: 32px !important;
}

#nav {
  top: 32px !important;
}

#sliding_action_bar_container {
  height: 32px !important;
}

#sliding_action_bar_container .bar-button {
  margin-top: -5px !important;
  height: 37px !important;
}

#headerBar .tab-text {
  font-size: 16px !important;
  font-weight: 400 !important;
}

.header-tab-title {
  line-height: 40px !important;
  height: 29px !important;
}

.header-tab-title.selected {
  border-bottom-color: white !important;
}
  
#browse-tabs {
  padding-top: 8px !important;
}
#browse-tabs .selected {
  font-weight: 400 !important;
}

#breadcrumbs {
  padding-top: 5px !important;
  background-color: #fb8521 !important;
  line-height: 28px !important;
}

#headerBar .tab-text,
#countSummary,
.header-tab-title {
  color: rgb(255,255,255) !important;
}

#countSummary {
  line-height: 18px !important;
}

#new-station-button {
  margin-top: -3px !important;
}

#extra-links-container {
  height: 25px !important;
}

.button.small {
  height: 25px !important;
  line-height: 25px !important;
}

/*Listen Now*/
div.situations-container .title,
div.recent-container .title,
div.recommended-header {
  font-size: 18px !important;
}

div.situations-container .subtitle, 
div.recent-container .subtitle {
  font-size: 16px !important;
}

div.situations-background {
  display: none !important;
}

div.situations-container .title {
  color: #333 !important;
}

div.situations-container .subtitle {
  color: #929292 !important;
}

	/*Genre Selector*/
div.situations-content {
  padding-top: 40px !important;
}

div.situations-view {
  margin: 0 !important;
}

	/*Situations Next Arrow*/
div.situations-container .lane-button.next {
  background: no-repeat url(https://play.google.com/music/sprites.png) -640px -2292px !important;
}
div.situations-container .cluster.has-next-page .lane-button.next {
  opacity: 1.0 !important;
}
div.situations-container:hover .cluster.has-next-page .lane-button.next {
  opacity: 1.0 !important;
}

	/*Situations Previous Arrow*/
div.situations-container .lane-button.prev {
  background: no-repeat url(https://play.google.com/music/sprites.png) -372px -870px !important;
}
div.situations-container .cluster.has-prev-page .lane-button.prev {
  opacity: 1.0 !important;
}
div.situations-container:hover .cluster.has-prev-page .lane-button.prev {
  opacity: 1.0 !important;
}
/*end Listen Now*/


div.queue-details .playing-from-title {
  margin: 0 !important;
}

/*Refresh station button*/
div.queue-details [data-id="refresh"]{
  position: relative !important;
  top: -5px !important;
  margin-right: 35px !important;
  font-size: 10px !important;
  padding: 3px 10px !important;
}

div.queue-details [data-id="refresh"] .icon{
  display: none;
}
/*End of Refresh station button*/

#extra-links-anchor {
  margin-top: 3px !important;
}

#extra-links-container {
  margin-top: 3px !important;
  margin-left: 10px !important;
}

#nav,
#nav-container,
.music-banner {
  width: 160px !important;
}

#content-container {
  margin-left: 160px !important;
}
  
.nav-item-container{
  font-size: 14px !important;
  padding: 3px 0 0 0!important;
  height: 24px !important;
  font-weight: 300 !important;
  line-height: 22px !important;
}

.nav-item-container.selected {
  font-weight: 400 !important;
}

.nav-section-header:nth-of-type(2) {
  display: none !important;
}

.nav-section-header {
  margin: 0px !important;
}

.nav-section-divider {
  margin: 0px 14px !important;
}

.nav-section-header,
.nav-item-container {
  text-indent: 10px !important;
}

.g-content {
    margin: 0 14px 30px 20px !important;
  }

.song-table [data-col="play-count"],
.song-table [data-col="duration"]{
	width: 55px !important;
  }

.modal-dialog {
  height: 300px !important;
  overflow-y: auto !important;
  top: 20px !important;
}

.simple-dialog-buttons {
  margin: 0 !important;
}
}
