    background-color: dimgrey !important;
    color: white !important;
}

.view-icon {

    padding: 1px 0 2px !important;
    filter: url("data:image/svg+xml;utf8,<svg xmlns=\'http://www.w3.org/2000/svg\'><filter id=\'grayscale\'><feColorMatrix type=\'matrix\' values=\'0.3333 0.3333 0.3333 0 0 0.3333 0.3333 0.3333 0 0 0.3333 0.3333 0.3333 0 0 1 1 1 1 0\'/></filter></svg>#grayscale") !important;

