css:
@-moz-document url-prefix('http://bookapp.book.qq.com/origin/chapter/read/') {
body {font-family:"Helvetica Neue",Helvetica Neue,Helvetica,Hiragino Sans GB,Microsoft Yahei,Arial,sans-serif !important; font-size:16px !important; background:#F9F9F9 !important;}
body * {border:0 none !important; background:transparent !important;}
body font {color:#B8BAB4 !important;}
strong, b {font-weight:normal !important;}
#mini_nav_qq_wrap,
.wrap,
.tagdh,
.typesFun,
#Top,
#footer,
#topad,
#controlLink,
iframe,
#FinalBox p:not(.ctitle) {display:none !important;}
#header {background:none !important;}
#FinalBox {margin:-10px auto 0 !important; width:980px !important; background:#F9F9F9; position:relative;}
#Position {color:#B8BAB4 !important;}
#Position #Pleft {float:none !important; padding:0 !important;}
#Position #Pleft a {color:#B8BAB4 !important; text-decoration:none !important;}
#Position #Pleft a:hover {text-decoration:underline !important;}
#FinalBox .ctitle {border-bottom:1px #DEDEDE solid !important; display:block !important; color:#696A6B !important; font-size:40px !important; padding:10px 0 30px !important; margin-bottom:30px !important;}
#FinalBox #content {width:100% !important; font-size:18px !important; font-family:"Helvetica Neue",Helvetica Neue,Helvetica,Hiragino Sans GB,Microsoft Yahei,Arial,sans-serif !important; line-height:180% !important; color:#606163 !important;}
#FinalBox #bottomPage {width:980px !important;}
#FinalBox #bottomPage .partPage{border-top:1px #DEDEDE solid !important; padding:20px 0 50px !important; display:block !important; width:100% !important; float:none !important; text-align:center !important; font-size:12px !important; color:#B8BAB4 !important;}
#FinalBox #bottomPage .partPage a {color:#B8BAB4 !important; text-decoration:none !important; font-weight:normal !important;}
#FinalBox #bottomPage .partPage a:hover {text-decoration:underline !important;}
#FinalBox #bottomPage .partPage span {color:#B8BAB4 !important;}
}
