css:
@-moz-document domain("narutoforums.com") {

/* ***** change font and make it larger/easier to read ***** */
/* ***** THANKS TO DREAM ***** */

legend, body, html, table, tbody, div, span, td, input, th, label, button, p, li, .panel, dt, .fieldset, .fieldset td, .fieldset p, .fieldset li, dd, .alt1, .alt1Active, .alt2, .alt2Active, .alt1 a, .alt1Active a, .alt2 a, .alt2Active a {
    font-family: "Helvetica Neue" !important;
}

.smallfont, label, legend, .pagenav, .thead, .fieldset, legend, table, tbody, td, input, select, button, p, li, div, span, .panel {
		font-size: inherit !important;
}

/* ***** page width ***** */

.page {
		width: 1183px !important;	
}
	

/* ***** remove underline from usernames ***** */
	
.bigusername {
		text-decoration: none !important;
}


/* ***** remove top bar, banner and top 'your ad here' ***** */
/* ***** THANKS TO GOLDEN CIRCLE ***** */

tr[background="http://static.anifreak.com/images/toperbg.jpg"] {
		display: none;
}

img[src="http://www.narutoforums.com/images/logo.gif"] { 
    display: none; 
}

div#pagenav_menu.vbmenu_popup + div { 
		display: none; 
}

/* ***** add album button fix ***** */

img[src*="images/buttons3/add_album.png"] {
		width: 0 !important;
		height: 16px !important;
		padding-left: 16px !important;
		background: url(http://www.anifreak.com/forums/skin_kakashi/buttons/add_album.png) !important;
}

/* ***** upload pictures button fix ***** */

img[src*="images/buttons3/upload_pictures.png"] {
		width: 0 !important;
		height: 16px !important;
		padding-left: 16px !important;
		background: url(http://www.anifreak.com/forums/skin_kakashi/buttons/upload_pictures.png) !important;
}

/* ***** profile down button fix ***** */

img[src*="images/buttons3/collapse_generic_collapsed.gif"] {
		width: 0 !important;
		height: 13px !important;
		padding-left: 13px !important;
		background: url(http://www.narutoforums.com/images/buttons/collapse_generic_collapsed.gif) !important;
}
}
