css:
@-moz-document url("chrome://stylish/content/manage-standalone.xul") {
#stylish-manage-standalone{
padding:8px !important;
background:#FFF -moz-linear-gradient(rgb(200, 210, 220), rgb(180, 190, 200))!important;
}

#styles-filter {
  -moz-appearance: none !important;
  padding: 2px 7px !important;
  background-color: hsla(210,30%,95%,.1) !important;
  background-image: -moz-linear-gradient(hsla(0,0%,100%,.6), hsla(0,0%,100%,.1)) !important;
  background-clip: padding-box !important;
  border: 1px solid hsla(210,15%,25%,.4) !important;
  border-color: hsla(210,15%,25%,.3) hsla(210,15%,25%,.35) hsla(210,15%,25%,.4) !important;
  border-radius: 3px !important;
  box-shadow: 0 1px 0 hsla(0,0%,100%,.3) inset,
              0 0 0 1px hsla(0,0%,100%,.3) inset,
              0 1px 0 hsla(0,0%,100%,.1) !important;
  font-size: 14px !important;
  line-height: 1.5 !important;
  -moz-transition-property: background-color, border-color, box-shadow !important;
  -moz-transition-duration: 150ms !important;
  -moz-transition-timing-function: ease !important;
}

#styles-filter:hover {
  background-color: hsla(210,30%,95%,.8) !important;
  border-color: hsla(210,15%,25%,.45) hsla(210,15%,25%,.5) hsla(210,15%,25%,.55) !important;
  box-shadow: 0 1px 0 hsla(0,0%,100%,.3) inset,
              0 0 0 1px hsla(0,0%,100%,.3) inset,
              0 1px 0 hsla(0,0%,100%,.1),
              0 0 3px hsla(210,15%,25%,.1) !important;
  -moz-transition-property: background-color, border-color, box-shadow !important;
  -moz-transition-duration: 150ms !important;
  -moz-transition-timing-function: ease !important;
}

#styles-filter:focus {
  background-color: hsla(210,15%,25%,.2) !important;
  box-shadow: 0 1px 1px hsla(210,15%,25%,.2) inset,
              0 0 2px hsla(210,15%,25%,.4) inset;
  -moz-transition-property: background-color, border-color, box-shadow !important;
  -moz-transition-duration: 10ms !important;
  -moz-transition-timing-function: linear !important;
}
#userstyles-heading > label{
margin:1px 2px 2px 5px !important;
}

#styles-sort{
width:auto !Important;
height:10px !Important;
  -moz-appearance: none !important;
  padding: 0px 5px !important;
  background-color: hsla(210,30%,95%,.1) !important;
  background-image: -moz-linear-gradient(hsla(0,0%,100%,.6), hsla(0,0%,100%,.1)) !important;
  background-clip: padding-box !important;
  border: 1px solid hsla(210,15%,25%,.4) !important;
  border-color: hsla(210,15%,25%,.3) hsla(210,15%,25%,.35) hsla(210,15%,25%,.4) !important;
  border-radius: 3px !important;
  box-shadow: 0 1px 0 hsla(0,0%,100%,.3) inset,
              0 0 0 1px hsla(0,0%,100%,.3) inset,
              0 1px 0 hsla(0,0%,100%,.1) !important;
  font-size: 13px !important;
  line-height: 1 !important;
  -moz-transition-property: background-color, border-color, box-shadow !important;
  -moz-transition-duration: 150ms !important;
  -moz-transition-timing-function: ease !important;
}
#styles-sort .menulist-label-box{
-moz-appearance:none !important;
background:none !important;
color:#000 !important;
}
#styles-sort .menulist-dropmarker{
display:none !important;
}


#userstyles-heading{
margin-bottom:4px !important;
background:-moz-linear-gradient(90deg,rgba(64,68,72,0.3) 1px,rgba(0,0,0,0) 1px) !important;
padding:0 0 4px 0 !important;
border-bottom:1px solid rgba(255,255,255,0.5) !important;
}




#styles{
-moz-appearance:none !important;
margin:3px 5px 5px 5px !important;
border:1px solid #789 !important;
box-shadow:0 0 4px 1px #789 !important;
background:rgba(255,255,255,0.6) !Important;
}


style-container > vbox > hbox{
border:none !important;
background:#ABC !important;
}



style-container > richlistitem{
background:rgba(0,0,0,0) !Important;
border-top:none !important;
border-bottom:1px solid #B3B8BE !important;
}
style-container > richlistitem[selected="true"]{
background:rgba(100,180,230,0.35) !Important;
color:#000 !important;
}


button > .button-box{
max-height:none !important;
min-height:0 !important;
height:auto !important;
padding:0 !important;
}
button {
  color:#171717 !important;
  -moz-appearance: none !important;
  padding: 3px 5px !important;
  background-color: hsla(210,30%,95%,.1) !important;
  background-image: -moz-linear-gradient(hsla(0,0%,100%,.6), hsla(0,0%,100%,.1)) !important;
  background-clip: padding-box !important;
  border: 1px solid hsla(210,15%,25%,.4) !important;
  border-color: hsla(210,15%,25%,.3) hsla(210,15%,25%,.35) hsla(210,15%,25%,.4) !important;
  border-radius: 3px !important;
  box-shadow: 0 1px 0 hsla(0,0%,100%,.3) inset,
              0 0 0 1px hsla(0,0%,100%,.3) inset,
              0 1px 0 hsla(0,0%,100%,.1) !important;
  font-size: 12px !important;
  text-align: center !important;
  -moz-transition-property: background-color, border-color, box-shadow !important;
  -moz-transition-duration: 150ms !important;
  -moz-transition-timing-function: ease !important;
}

button:hover {
  background-color: hsla(210,30%,95%,.8) !important;
  border-color: hsla(210,15%,25%,.45) hsla(210,15%,25%,.5) hsla(210,15%,25%,.55) !important;
  box-shadow: 0 1px 0 hsla(0,0%,100%,.3) inset,
              0 0 0 1px hsla(0,0%,100%,.3) inset,
              0 1px 0 hsla(0,0%,100%,.1),
              0 0 3px hsla(210,15%,25%,.1) !important;
  -moz-transition-property: background-color, border-color, box-shadow !important;
  -moz-transition-duration: 150ms !important;
  -moz-transition-timing-function: ease !important;
}

button:hover:active {
  background-color: hsla(210,15%,25%,.2) !important;
  box-shadow: 0 1px 1px hsla(210,15%,25%,.2) inset,
              0 0 2px hsla(210,15%,25%,.4) inset;
  -moz-transition-property: background-color, border-color, box-shadow !important;
  -moz-transition-duration: 10ms !important;
  -moz-transition-timing-function: linear !important;
}


}
