css:
body 
{
background: gray !important;
}


.main-body
{
background-color: #000000 !important;
border-style: none !important;
border-radius: 10px 10px 10px 10px !important;
margin-top:-14px !important;
margin-bottom:6px !important;
}

.navbar-inner 
{
background-color: #000000 !important;
background-image: none !important;
}



div.span9 div.review-progress
{
background-color: #CCFF99 !important;
border-radius: 10px 10px 10px 10px !important;
}
.well
{
background-color: #66CCFF !important;
border-radius: 10px 10px 10px 10px !important;
}
div#ex-defaults-box ol.span9,div#ex-user-box ol.span9
{
background-color: #66CCFF !important;
border-radius: 10px 10px 10px 10px !important;
padding-top:10px !important;
}


h1.content
{
color: #FF3399 !important;
text-shadow: 
-1px -1px 1px black,
1px -1px 1px black,
-1px 1px 1px black,
1px 1px 1px black,
-2px -2px 3px #FFFF00,
2px -2px 3px #FFFF00,
-2px 2px 3px #FFFF00,
2px 2px 3px #FFFF00,
-3px -3px 3px #FFFF00,
3px -3px 3px #FFFF00,
-3px 3px 3px #FFFF00,
3px 3px 3px #FFFF00;
}

span.mid,div.quote small
{
color: #BB3399 !important;
font-weight:bolder !important;
}


div.span9 small
{
color: #66FFFF !important;
text-shadow: #66FFFF 0px 0px 0px !important;
margin-left:14px !important;
}


.continue-button
{
background-color: #000000 !important;
color: #CC66FF !important;
font-weight:bolder !important;
}


.ex-sys-box-tab, .ex-mine-box-tab, .ex-user-box-tab,.note-user-box-tab
{
background-color: #99CCFF !important;
color: #000000 !important;
border-style: none !important;
}


ul.nav-tabs li.active a
{
background-color: #CC99FF !important;
color: #000000 !important;
border-style: none !important;
}


div.span9, div.quot, div.note
{
color: #99CCFF !important;
}


span.content, .text,ol.span7,div.sentence,.step_1,.step_2,.prompt-right
,.inactive,.enex,.cnex, .pull-left
{
color: #000000 !important;
}
.index
{
color: #66CCFF !important;
text-shadow: none !important;
}


.page-header, div.title,.forgot-btn
{
color: #DD6600 !important;
}

.sub-nav 
{
background-color: #000000 !important;
background-image: -moz-linear-gradient(center top , #000000, #000000) !important;
background-repeat: repeat-x !important;
border: 0px solid #E5E5E5 !important;

}
ul.nav-pills
{
margin-left:15px !important;
margin-right:10px !important;
}
.logo a img {
opacity:0 !important;   
}

div.logo div.container {
background-color: #000000 !important;
border-radius: 7px 7px 7px 7px !important;
padding-bottom:5px !important;
background-image: url("http://24.media.tumblr.com/tumblr_maw5xgAWmq1rytmx2o1_1280.png") !important;
background-repeat: no-repeat !important;
background-position: top center;
width: 99% !important;
}


.pull-right, div.span11 h3, h3.span4
{
color: #9494B8 !important;
}

.span5 a
{
color: #00FFCC !important;
}

h4.teamname a, div.span7 h4 a
{
color: #66CCFF !important;
}

ul#forum-box a, div.modal-header h3 small, div.span5 a, div.span6 a
{
color: #DD0000 !important;
font-weight:bolder !important;
}
ul#forum-box li.active a, div.modal-header h3, div#level-setting h3, div#level-setting label
{
color: #000000 !important;
font-weight:bolder !important;
}

div.pull-right a
{
color: #990099 !important;
font-weight:bolder !important;
}

span.user a,span.forum a, div.content span,div.pull-left div.author
{
color: #339900 !important;
}

span.counter
{
color: #FF8533 !important;
}

div.start-review 
{
background-color: #00AA99 !important;
}

small, span.unit
{
color: #AA0000 !important;
}

button,a,td{text-shadow: #0000ff 0px 0px 1px !important;}
span{text-shadow: #0033ff 0px 0px 1px !important;}

div {text-shadow: #0033ff 0px 0px 1px !important;}

p,dd,pre,h2,h3,font,b {text-shadow: #0033ff 0px 0px 1px !important;}
