css:
/* Ylilauta Icy Terminal Style, version 1.2 */

@namespace
 url(http://www.w3.org/1999/xhtml);

@-moz-document
 url-prefix(http://ylilauta.org),
 url-prefix(http://ylilauta.fi),
 url-prefix(http://boards.ylilauta.org),
 url-prefix(https://ylilauta.fi),
 url-prefix(https://ylilauta.org),
 url-prefix(https://boards.ylilauta.org),
 domain("www.ylilauta.org") {

/* Page background */
* {
	font-size: 11px !important;
	font-family: 'Courier New', Courier, 'Nimbus Mono L', monospace !important;
}
body {
	color: #AAAAAA !important;
	background: #0A0A0A !important;
}

h1 {
	color: #FFFFFF!important;
}

/* The lines between posts */
hr {
	border-bottom: 1px solid #FFFFFF !important;
}

/* Links */
a {
	color: #009ACD !important;
}
a:hover {
	color: #66FF66 !important;
}

/* Tooltips, like message preview hovering */
.tooltip {
	border: 1px dotted #000000 !important;
	background-color: #383838 !important;
}

/* The sliding-down settings-box */
#settingsbox {
	background-color: #383838 !important;
	border: 1px solid #009ACD !important;
}

/* Sidebar */
#left {
	background: #0A0A0A !important;
}
#left ul a:hover {
	background: #206098 !important;
	border-radius: 2px !important;
}
#left li.header {
	background-color: #009ACD !important;
	color: #0A0A0A !important;
	-webkit-border-radius: 2px !important;
	-o-border-radius: 2px !important;	
	-moz-border-radius: 2px !important;
	border-radius: 2px !important;
	font-weight: bold !important;
}

/* Front page */
#front h3 {
	background-color: #009ACD !important;
	color: #000 !important;
}
#front h3 a {
	color: #383838 !important;
	text-decoration: none !important;
}
#front h3 a:hover {
	color: #000 !important;
	text-decoration: underline !important;
}
.box {
	background-color: #383838 !important;
}
#tabs li {
	background-color: #666 !important;
	border-top: 1px solid #999 !important;
	border-right: 1px solid #999 !important;
}
#tabs li:first-of-type {
	border-left: 1px solid #888888 !important;
}
#tabs li.cur {
	font-weight: bold !important;
	padding-top: 6px !important;
}

/* This message will be sent as a reply ... */
.infobar {
	background-color: #009ACD !important;
	color: #000 !important;
	font-weight: bold !important;
}

/* The posting form */
#postform td.label {
	border: 1px solid #444 !important;
	background-color: #383838 !important;
	color: #009ACD !important;
	font-weight: bold !important;
}

/* Posts */
.file p {
	border: 2px dashed #aaaaaa !important;
}
.omitted {
	color: #707070 !important;
}
.answer {
	padding: 0 0 5px 0 !important;
	padding-left: 8px !important;
	padding-top: 4px !important;
	padding-right: 16px !important;
	background-color: #191919 !important;
}
.highlighted {
	border: 3px dashed #63a3dc !important;
}
.fileinfo a {
	text-decoration: none !important;
}
.postsubject {
	font-weight: bold !important;
	color: #009ACD !important;
}
.postername {
	color: #FFFFFF !important;
	font-weight: bold !important;
}
.sage {
	color: #66ff66 !important;
	text-decoration: underline !important;
}
.postertrip {
	color: #BBBBBB !important;
}
.quote {
	color: #00AA00 !important;
}
.bluequote {
	color: #1874CD !important;
}
.spoiler {
	color: #222222 !important;
	background-color: #222222 !important;
}
.spoiler a {
	color: #222222 !important;
}
.spoiler:hover .quote {
	color: #009ACD !important;
}
.spoiler:hover a {
	color: #009ACD !important;
}

/* Board pagination on the bottom of the page */
.pagination {
	background-color: #191919 !important;
	border: 1px solid #009ACD !important;
}
.disabled {
	color: #555 !important;
}

/* Banned -page */
#banned {
	background-color: #000000 !important;
}
#banned h1 {
	background-color: #009ACD !important;
	color: #000000 !important;
}

/* At least hidden threads -page uses this */
.list td {
	border: 1px solid #d9bfb7 !important;
}
.list .threadinfo {
	border-top: 1px solid #d9bfb7 !important;
}

/* Followed threads -box */
#followedtitle {
	background-color: #009ACD !important;
	color: #000000 !important;
}
#followedbg {
	border: 1px dotted #009ACD !important;
	background-color: #383838 !important;
}
#followedfunctions {
	border-top: 1px solid #D9BFB7 !important;
}

}
