css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ttsearch.net") {

body {
    background-color: #333 !important;
    margin-left: 11% !important;
}
#container {
    margin: 0 auto !important;
}
#center {
    background-color: #FCFCFF !important;
    width: 545px !important;
}
#center div form > span:last-child {/* XXXスレッド (xxxレス, x/分)の位置ずらし */
    padding-left: 13em;
}
#center > form {/* 検索結果一覧の左側余白 */
    padding-left: 9px !important;
}
#center > div {
    border: hidden !important;
}
div {/* 検索結果 */
    line-height: 108% !important;
    margin-bottom: 6px !important;
}
td#center form > div + p {/* 空 pタグ */
    display: none !important;
}
#right {
    border-top: hidden !important;
}
.a50 {/* スレ最新50へのリンク */
    visibility: hidden;
}
#top,
#bottom,
td#center > form > div:first-child,/* 選択したスレッドに タグを追加 */
input.ch/* チェックボックス */ {
    display: none !important;
}


/*
a {
    text-decoration: none !important;
}*/
/* 検索結果の更新確認日時表示色 */
/*.update {color: #e88ff6 !important;}*/

/* サーバ名表示色（板名なんだけど板名とは違う） */
.board {color: #b3b19c !important; text-decoration: none !important;}
}
