css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/')   {
#dashboard_controls_open_blog.controls_section,
div.open_blog.with_subtitle, .open_blog,
#popover_button_blogs, .open_blog.with_subtitle,
div.open_blog.with_subtitle,
#posts_control,
#dashboard_controls_open_blog > LI:nth-child(5),
#dashboard_controls_open_blog > LI:last-child {
  border-color: rgba(0,0,0,0) !important;
  box-shadow: none !important;
  border-radius: 0px !important;
  background-color: rgba(255,255,255,0) !important;
  background-image: none !important;
}
#posts_control {
  position: relative !important;
  background: transparent !important;
}
#dashboard_controls_open_blog.controls_section:before {
  position: absolute !important;
  content: "" !important;
  top: -10px !important;
  left: -5px !important;
  width: 215px !important;
  height: 175px !important;
  background-image: url("data:image/jpeg;base64,") !important;
  background-size: 215px 175px !important;
  z-index: 0 !important;
  border: outset 5px rgba(0,0,0,.5) !important;
}
#right_column > DIV:nth-child(2) > A {
  position: relative !important;
  top: 8px !important;
  line-height: 24px !important;
}
#dashboard_controls_open_blog .posts .hide_overflow,
#dashboard_controls_open_blog .followers .hide_overflow,
#dashboard_controls_open_blog .drafts .hide_overflow {
  padding-left: 25px !important;
}
#popover_button_blogs .hide_overflow,
#popover_button_blogs .hide_overflow a,
#dashboard_controls_open_blog .posts .hide_overflow,
#dashboard_controls_open_blog .followers .hide_overflow,
#dashboard_controls_open_blog .drafts .hide_overflow {
  color: rgba(255,255,255,.35) !important;
}
#dashboard_controls_open_blog .selected .posts .hide_overflow,
#dashboard_controls_open_blog .selected .posts .count,
#dashboard_controls_open_blog .selected .followers .hide_overflow,
#dashboard_controls_open_blog .selected .followers .count,
#dashboard_controls_open_blog .selected .drafts .hide_overflow {
  color: rgba(255,255,255,.75) !important;
}
#dashboard_controls_open_blog .posts,
#dashboard_controls_open_blog .followers,
#dashboard_controls_open_blog .drafts {
  background-image: none !important;
}
#posts_control > .posts:before,
#dashboard_controls_open_blog .followers:before,
#dashboard_controls_open_blog .drafts:before {
  position: absolute !important;
  content: "" !important;
  top: 6px !important;
  left: 4px !important;
  width: 56px !important;
  height: 24px !important;
  background-size: 56px 24px !important;
  border-radius: 4px !important;
}
#dashboard_controls_open_blog .selected .posts:before,
#dashboard_controls_open_blog .selected .followers:before,
#dashboard_controls_open_blog .selected .drafts:before {
  background-image: url("data:image/jpeg;base64,") !important;
}}
