css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("news.mynavi.jp") {
body {
    background: none !important;
}
div#str-container div#contents {
    background: none !important;
} 
#str-container {/* 全体をもっと右に */
    margin: 0 auto 0 6% !important;
}
#contents {/* コンテンツ上部に余白を */
    padding: 12px 24px 0 !important;
}
.str-lyt-page-nav-03-l {/* 1段階縮小時 上部ページナビのずれ修正 */
    border-right: none !important;
    width: 594px !important;
}
.articleContent {/* 記事本文とページナビの間 */
    margin-top: 10px !important;
}


#str-header,
#header-mainnav,
.str-icon-social,
.str-lyt-page-nav-03 + div:not(.articleContent),/* 本文上の「xxx」の記事をお探しですか？ */
[id^="ad"],/* 左カラム - Ad */
#blogTensai,/* 記事下部 共有ボタンなど */
#str-contentsleft > .lyt-box-01:not(:first-child),/* 本文下いろいろ ※不具合あるかも */
dl.equalize,/* 関連したタグ */
.topicPath,/* 各種トップページへのリンク */
#str-contentsleft > div:nth-last-child(4),/* 特設サイトの情報 */
#dummy01,/* 特設サイトの情報body */
.fb-like01,/* FB */

[id^="r_ad"],/* 右カラム - Ad */
#str-contentsright > div:nth-last-child(1),/* マイナビニュースマガジンbody */
#str-contentsright > div:nth-last-child(2),/* マイナビニュースマガジン */
#str-contentsright > div:nth-last-child(5),/* 特別企画 */

#mynavibear_small_box,/* いぬ */
#tbadge,/* Follow me */
#str-footer{
    display: none !important;
}
}
