css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.generation-nt.com") {

#bodybox {
    width:99% !important;
    min-width: 480px !important;
    max-width: 1400px !important;
}

#bandeau, #footer {
width:100% !important;
}

#new_menu {
   margin:auto !important;
   background: #d9e3eb !important;
}

#leftContent, #colonneGauche,.onRight {
width:25% !important;
min-width:182px !important;
float: left !important;
background-color: #ebf0f4 !important;
}

ul.pointilles > li > a {
background-image:none !important;
}


#rightContent, colonneCentre, .onLeft {
width:71% !important;
margin-left:1% !important;
float: left !important;
}

.sommaire2, #footer  {
width:100% !important;
background-image : none !important;
background-color:#2C80B7 !important;
height:100% !important;
}

#menu {
width:100% !important;
margin:auto !important;
}

#content2 {
background-image : none !important;
height:auto !important;
}

.sommaire2 > inner {
background-color:#2C00B7 !important;
}

html > body #logo {
padding:0 !important; 
margin-left:auto !important;
margin-right:auto !important;
width:190px !important;
height:85px !important;
text-align:center !important;
margin-left:48% !important;
}

[class^="pub"],[class$="pub"],[class$="gbprod"],[class^="eprix"], #menuDL:first-child li, iframe, [class^="innerpub"] {
width:0 !important;
height:0 !important;
display:none !important;
}

div #rightContent div[id^="gus"] {
width:0 !important;
height:0 !important;
display:none !important;
}

#rightContent > table {
width:0 !important;
height:0 !important;
display:none !important;
}

div #leftContent :first-child div[class^="inner"] {
width:0 !important;
height:0 !important;
display:none !important;
} 
 #eugene,[class^="toshiba"],#leftToshiba, #toshiba_2,#toshiba_1
{
width:0 !important;
height:0 !important;
display:none !important;
} 

widpub,.googleperso,[class^="itx"]{
display : none !important;
}

a.iAs {
text-decoration:inherit !important;
color:inherit !important;
border-bottom:none !important;
}

#PosterCommentaires > h2 , .textarea_comment {
width : 100% !important;
background-repeat:no-repeat !important;
}

tr:first-child td { width:100% !important;}

td > table > tbody > tr:first-child td { 
width:auto !important;
}
.table_navig > tbody > tr > td { width:50% !important;}
.table_navig { width:100% !important;}


.onLeft { 
width:70% !important;
}
.onRight { 
width:27% !important;
}

#commentaires {
-moz-border-radius : 1em !important;
padding-left:0.7em !important;
padding-right:0.7em !important;
}

.texte { 
margin-left:1em !important;
margin-right:1em !important;
}
.line-even {
background-color: #D6D4D1 !important;
}

.triche,#colonneCentre {
background-image:none !important;
}
.dossiersList > ul > li > a.cmt {
padding:0px !important;
margin:0px !important;
width:auto !important;
}

.inner > .ligne {
height:auto !important;
}
.blockDossier {
background-image:none !important;
}
.dossiersList > ul > li > a.cmt > span{
width:auto !important;
}

#content {
width:100% !important;
}
#colonneCentre {
width:80% !important;
}

#colonneGauche {
width:auto !important;
margin:0px !important; padding:0px !important;
}
}

@-moz-document url("http://www.generation-nt.com/") {
#colonneCentre {
float:left !important;
width:80% !important;
}
.blockBleu , .blockBleu .inner, .triche {
background-image:none !important;
}

.blockBleu { 
background-color:black !important;
}

.onRight {
display : none !important;
} 

#new_menu {
margin:auto !important;
display:none !important;
}

td {
margin:0px !important;padding:0px !important;
max-width: 80px !important;
border:2px green solid;
border:none;
}
tr { 
margin:0px !important;padding:0px !important;
width: 100% !important;
 }

.ads {
display : none !important;
background-color:red !important;
}


}
