css:
@-moz-document url-prefix("https://www.podiumcafe.com"), url-prefix("http://www.podiumcafe.com") {

@media screen {

body { background-color: #888 !important; zoom: 100% !important; font-size: 80% !important; }
.sbn-page-layout {background-color: #f5f5f5 !important; }
.sbn-pl-home-after-cover {background-color: #f5f5f5 !important; }
.sbn-river-standard-chunk-stack-col { border-color: #f5f5f5 !important; }
.sbn-river-standard-chunk-vert-col  { border-color: #f5f5f5 !important; }
.sbn-entry-related-content { background-color: #eee !important; border-color: #eee !important; }

.sbn-network-nav-item { display: none !important; }
.sbn-network-nav-list { height: 46px !important; }
.sbn-user-nav-facebook-connect { display: none !important; }
.sbn-river-horizontal-breaker { display: none !important; }
#idSiteMeterHREF { display: none !important; }
.sbn-entry-next-read {display: none !important; }
div.group-row {display: none !important; }
.sbn-site-nav-utility {display: none !important; }
.sbn-site-nav-image { display: none !important; }
.sbn-site-nav-label-subtitle { display: none !important; }
.sbn-river-standard-chunk-padded { padding-right: 0 !important; }

.sbn-pl-sbn-dfl-comments-sidebar { visibility: hidden !important; }

.sbn-site-nav-inner { background: none !important; }
.sbn-site-nav { position: absolute !important; top: 52px !important; left: 100px !important; }
.sbn-pl-home-after-cover { position: static !important; }
.sbn-pl-home-inner-content { position: static !important; }
.sbn-site-nav-sections { margin: 0 !important;  }
.sbn-site-nav-sections-item { cursor: default; }
.sbn-entry-body-inner p { text-align: justify !important; }
.sbn-entry-body-inner img { padding-left: 10px !important; padding-right: 10px !important; }

.vox-comments .comment { font-family: arial !important; padding-top: 2px !important; padding-bottom: 0px !important; padding-right: 20px !important; }
.vox-comments .comment p { font-family: arial !important; text-align: justify !important; }
.vox-comments .comment h5 { font-family: arial !important;  }
.vox-comments .comment .by { clear: none !important; }
.vox-comments .cform { background-color: #ddd !important; padding-top: 8px !important; }
.vox-comments .cform input { font-family: arial !important; border: 1px solid #aaa !important; }
.vox-comments .cform textarea { font-family: arial !important; border: 1px solid #aaa !important; }

.comment_master_list .citem .new { background-color: #fFffd1 !important; }
.comment_master_list .citem .cfocus { background-color: #fFf5c1 !important; }

}

}
