css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("boards.4chan.org")
{

*{
border:none!important;
background:none!important;
font-family:calibri!important;
overflow-x: hidden;
}
    
input,textarea{background:white!important;}

div.post.reply{
width:38%!important;
margin-left:25%!important;
padding-top:5px!important;
padding-left:5px!important;
}

.reply:target, .reply.highlight{
background-color:#eee!important;
border:none!important;
}
    
.dd-menu li{
padding:0!important;
}
    
.burichan_new .backlink a:hover, .yotsuba_b_new .backlink a:hover  ~ div{
display:none!important;
}
    
blockquote.postMessage{
max-width:900px!important;
text-align:justify!important;
}


html{
background:#F7F7F7!important;
background-image: url('/*[[img]]*/')!important;
background-position: right bottom!important;
background-attachment: fixed!important;
background-repeat: no-repeat!important;
background-size: 33%!important;
}

a>img{
box-shadow: grey 0 0 3px!important
}

form>table{
padding: 4px !important;
}

.reply,.replyhl{
background: #ffffff!important;
margin: 15px 0px!important;
border-radius: 5px!important;
box-shadow: 0 0 2px rgba(0,0,0,0.3)!important;
}

blockquote,.quotejs,span.quote {
color: #999!important;
}

a.fileThumb img {
margin: 10px !important;
border: none !important;
}

img#recaptcha_reload,
img#recaptcha_switch_audio,
img#recaptcha_whatsthis{
box-shadow:none!important;
}

span.subject {
color:#707070 !important;
}

span.postNum,a,span.fileText,div.backlink,span.spoiler{
color:#ccc!important;
}


img.bottomad,
div#absbot,
div.center.middlead,
div.center,
hr, 
div.sideArrows,
strong,
img#toggleMsgBtn,
img.middlead,
div#styleSwitcher,
div#boardNavDesktopFoot,
span.postertrip,
.click-me{
display:none!important;
}

div.pagelist.desktop,form{
margin-bottom:50px!important;
}

img.topad{
width:37%!important; height:37%!important;
}

label,li,span.posteruid,
span.password,
div.passNotice,
td,
div#ctrl,
span.navLinks,
#search-label-bottom,
.button,
span.password,
div.passNotice,
td,
span.deadlink
{
color:grey!important;
}

a.quotelink:link,a.quotelink:link:hover{
color:#d6d6d6!important;
}

a.quotelink:visited,a.quotelink:visited:hover{
color:#dedede!important;
}

span.posteruid, span.hand{
color:#ccc!important;
}
      
s{
background-color:#FFC0CB!important;
}

s:hover{
color:black!important;
}

span.hand:hover,div.error,S,span.name,.dateTime,a.replylink:link,a:hover{
color:/*[[pink]]*/!important;
}

div.fileText{
color:#ddd!important;
}

div.navLinks.desktop{
color:rgba(0,0,0,0)!important;
}

span#togglePostFormLink.button{
color:#800000!important;
}

.bottomCtrl.desktop{
background:white!important;
padding:7px!important;
border-radius:5px!important;
box-shadow:0 0 2px rgba(0,0,0,0.3)!important;
}

#quickReply{
padding-left:5px!important;
padding-top:5px!important;
padding-right:5px!important;
padding-bottom:0!important;
}
    

}
