css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

#appmenu-button {
  padding: 0 5px !important;
  margin-right: -3px !important;
  background: none !important;
  border-color: transparent !important;
  border: 0px !important;
  box-shadow: none !important}

#appmenu-button > .box-inherit.button-box > .box-inherit > .button-icon {
  margin-top: 2px !important;
  list-style-image: url(data:image/png;base64,) !important}

#main-window[privatebrowsingmode=temporary] #appmenu-button > .box-inherit.button-box > .box-inherit > .button-icon,
#appmenu-button:hover > .box-inherit.button-box > .box-inherit > .button-icon,
#appmenu-button:active > .box-inherit.button-box > .box-inherit > .button-icon,
#appmenu-button[open] > .box-inherit.button-box > .box-inherit > .button-icon {
  background: rgba(151,152,153,.05)
                    -moz-linear-gradient(rgba(251,252,253,.95), rgba(246,247,248,.47) 49%, 
                                            rgba(231,232,233,.45) 51%, rgba(225,226,229,.3)) !important;
  border-color: rgba(0,0,0,.12) rgba(0,0,0,.19) rgba(0,0,0,.38) !important;
  border: 5px !important}

#appmenu-button > .box-inherit.button-box > .button-menu-dropmarker {
  display: none !important}
