css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://secure.solve360.com/mobile") {

   ul li {font-size:14px!important;font-weight:normal!important;}

   ul#menu li, ul#calendars li, ul#followups li, ul#actions li, ul#contacts li, ul#blogs li, ul#companies li {
    margin: 15px 30px 15px 30px!important;
	-webkit-box-shadow:inset 0px 1px 0px 0px #ffffff!important;
	box-shadow:inset 0px 1px 0px 0px #ffffff!important;
	background-color:#ededed!important;
	-webkit-border-radius:6px!important;
	border-radius:6px!important;
	border:1px solid #dcdcdc!important;
	color:#777777!important;
	font-family:arial!important;
	font-size:16px!important;
	font-weight:400!important;
	padding:6px 24px!important;
	text-decoration:none!important;
	text-shadow:1px 1px 0px #ffffff!important;
    }
   ul#menu li:hover, ul#calendars li:hover, ul#followups li:hover, ul#actions li:hover, ul#contacts li:hover, ul#blogs li:hover, ul#companies li:hover {
	background-color:#dfdfdf!important;
    }
   ul#menu li:active, ul#calendars li:active, ul#followups li:active, ul#actions li:active, ul#contacts li:active, ul#blogs li:active, ul#companies li:active {
	top:1px!important;
	background-color:#dfdfdf!important;
    }

   ul.x-add-act-list li {
    margin: 15px 40px 15px 0px!important;
	-webkit-box-shadow:inset 0px 1px 0px 0px #ffffff!important;
	box-shadow:inset 0px 1px 0px 0px #ffffff!important;
	background-color:#ededed!important;
	-webkit-border-radius:6px!important;
	border-radius:6px!important;
	border:1px solid #dcdcdc!important;
	color:#777777!important;
	font-family:arial!important;
	font-size:14px!important;
	font-weight:400!important;
	padding:6px 24px!important;
	text-decoration:none!important;
	text-shadow:1px 1px 0px #ffffff!important;
    }
   ul.x-add-act-list li:hover {
	background-color:#dfdfdf!important;
    }
   ul.x-add-act-list li:active {
	top:1px!important;
	background-color:#dfdfdf!important;
    }

   li.iph-pr0, li.iph-pr1, li.iph-pr2, li.iph-pr3, li.iph-pr4, li.iph-pr5 {margin-bottom: 8px!important;}

   ul#menu li a, ul#calendars li a, ul#followups li a, ul#actions li a, ul#contacts li a, ul#blogs li a, ul#companies li a {
     padding: 0px 0px 0px 0px!important;
     margin: 0px 0px 0px 0px!important;}

   ul#menu li {font-size:24px!important;}
   ul#calendars li {font-size:24px!important;}
   ul#followups li {font-size:24px!important;}
   ul#actions li {font-size:24px!important;}
   ul#contacts li {font-size:24px!important;}
   ul#blogs li {font-size:24px!important;}
   ul#companies li {font-size:24px!important;}
   ul#menu li a {font-size:20px!important;}
   ul#calendars li a {font-size:16px!important;}
   ul#followups li a {font-size:16px!important;}
   ul#actions li a {font-size:16px!important;}
   ul#contacts li a {font-size:16px!important;}
   ul#blogs li a {font-size:16px!important;}
   ul#companies li a {font-size:16px!important;}

   ul.panel#contacts li a {font-size:18px!important;}
   ul.panel#blogs li a {font-size:18px!important;}
   ul.panel#companies li a {font-size:18px!important;}

   ul.contact-view-acts, ul.contact-view-acts li {padding:0px 0px 0px 0px!important;margin-right:0px!important;}
   ul.contact-view-acts li div {padding-right:0px!important;margin-right:0px!important;}
   table.ax-view {padding:2px 0px 2px 2px!important;margin-right:0px!important;}
   td.ax-col-type {width:20px!important;}
   td.ax-tasklist {margin-right:0px!important;padding-right:10px!important;}
   div.ax-tasklist-title {margin:0px 0px 0px 0px!important;padding:0px!important;}
   div.ax-task {margin:0px 0px 0px 10px!important;padding:5px 0px 5px 5px!important;}
   div.ax-task div.title {margin:0px 20px 0px 10px!important;}
   table.ax-view td.ax-col-user {display:none!important;}
   table.ax-view table.x-act-field {margin:0px 0px 0px 0px!important;padding:0px 0px 0px 0px!important;}

   div.iph-search img {left:108px!important;}
   div.iph-search .reset {right:1px!important;}
   ul#menu li, ul.panel#contacts li, ul.panel#blogs li, ul.panel#companies li {padding-right:0px!important;}

   div.panel {
   position:absolute!important;
   width:inherit!important;
   right:0px!important;
   }
   div.panel div.row label {
   position:absolute!important;
   left:0px!important;
   }
   
   div.panel.contact-view {
   width:inherit!important;
   left:0px!important;
   }

   form.panel {
   width:inherit!important;
   }
   form.panel div.row {
   padding:10px 2px 10px 0px!important;
   }
   form.panel div.row label {
   position:absolute!important;
   left:0px!important;
   }
   form.panel div.row input {
   width:240px!important;
   background-color:rgb(235,235,235)!important;
   padding:0px 2px 0px 2px!important;
   right:0px!important;
   }
.ax-email-table tr {height:20px!important;}

form.panel div.row {min-height: 22px!important;}

div.toggle, div.toggle span {
   height: 24px!important;
   font-size:16px!important;
   line-height:26px!important;
   }

}
