css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(boards.4chan.org) {
/* center the page */
body {
    width: 810px;
    margin-left: auto !important;
    margin-right: auto !important;
    background: #EEF2FF url(http://static.4chan.org/image/fade-blue.png) top center repeat-x !important;
}

body.is_catalog {
    width: auto !important
}


/* light colors for headings */
.postingMode {
    background-color: rgba(0, 0, 15, 0.45) !important;
}

.boardBanner {
    color: rgba(0, 0, 15, 0.7) !important;
}


/* prettify */
.summary.desktop,
.post {
    display: block;
    clear: both;
}

.replyContainer {
    background-color: white;
    border: 1px solid silver !important;
    clear: both;
}

.replyContainer + .replyContainer {
    margin-top: -1px;
}

.replyContainer .post.reply {
    background: white !important;
    margin: 0 !important;
    border: none !important;
}

.replyContainer .post.reply.highlight {
    background: #D6DAF0 !important;
    margin: 0 !important;
    border: none !important;
}

.postMessage {
    margin: 0 25px;
    color: black;
    max-width: 740px !important;
}

.thread {
    margin-bottom: 25px !important;
    border: none !important
}

blockquote.postMessage {
    margin: 1em 1.8em;
}

.post.reply {
    box-shadow: unset !important
}

font.unkfunc,
span.quote {
    color: #006622 !important;
}

a,
a.quotelink {
    color: #34345c !important;
}

a:hover,
a.quotelink:hover {
    color: #0055ff !important;
}

s,
s:hover,
#quote-preview.reveal-spoilers s {
    color: black !important;
    background-color: rgba(0, 0, 0, 0.1) !important;
    text-decoration: none !important;
}

hr {
    visibility: hidden;
    margin: 0;
    padding: 0;
    width: 0;
    height: 0;
}

img.title {
    display: none;
}

.board {
    background-color: #EEF2FF
}


/* extension buttons */
.post.op .threadHideButton,
.fileText .extButton,
.postInfo .extButton,
.sideArrows .replyHideButton,
.hide_thread_button span,
.sideArrows {
    opacity: 0;
}

.post.op .threadHideButton:hover,
.fileText .extButton:hover,
.postInfo .extButton:hover,
.sideArrows .replyHideButton:hover,
.hide_thread_button span:hover,
.sideArrows:hover {
    opacity: 1;
}

.post.op .threadHideButton,
.hide_thread_button,
.sideArrows {
    position: absolute;
    left: 50%;
    margin-left: -422px !important;
    padding-top: 1px;
}


/* reCAPTCHA div position workaround */
body > div[style^="background-color: rgb(255, 255, 255); border: 1px solid rgb(204"] {
    margin-left: 0em !important;
}
}
