css:
@-moz-document domain(tumblr.com) {
body {

background: url('http://i.imgur.com/KWw03.png') bottom right #a10000 no-repeat fixed !important;

background-attachment: fixed !important;

}


#logo {height: 0 !important;

	width: 0 !important;

	padding-left: 72px !important;

	padding-top: 70px !important;

	background: url(http://i.imgur.com/hgHxf.png) no-repeat !important;}

ol#posts li.notification {



  background-color: #ffffff !important;



  border-top: 1px solid #f00000 !important;



  border-left: 1px solid #f00000 !important;



  border-bottom: 1px solid #f00000 !important;



  border-right: 1px solid #f00000 !important;



  color: #292929 !important;



  text-decoration: none !important;



}



.answer_container {



  background-color: #000000 !important;



}


#posts .notification a:not(.avatar_frame) {



color: rgba(170, 0, 10, 1) !important;



font-weight: bold;



text-decoration: none;



border-bottom: 0px solid #ffffff !important;



-webkit-background-clip: padding-box;



}







#posts .notification blockquote {



  border-left: solid 3px #000000 !important;



}







#posts .notification .avatar_frame {



background-color: #ffffff !important;



border: 1px solid #ffffff !important;



position: absolute;



top: -1px;



left: -55px;



overflow: hidden;



display: inline-block;



}





.post.is_mine.with_avatar.new_post {



  color: #000000 !important;



  font-family:courier !important;



  background: rgba(160,0,20,0.50) !important; 


opacity:1.0 !important;  



}





#posts .post.new_post .new_post_label {


opacity: 1.0 !important;



font-size: 15px;



font-weight: normal;



color: #000000;



text-transform: lowercase;



text-align: center;



width: 74px;




}







#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {



  background: rgba(55, 10, 10, .5) !important; 



}







.selection_nipple:not([class="selection_nipple white"]) {



border-bottom: solid 11px rgba(0,00,00, 0.50)  !important;



}







#content_top {



  width: 0px !important;



  height: 0px !important;









}



.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) {



background: none !important;



width: 0px !important;



height: 0px !important;



margin-top: 0px !important;



border-bottom: solid 11px rgba(00,00,00, 0.50) !important;



border-right: 11px solid transparent !important;



border-left: 11px solid transparent !important;



}









#content_bottom {



  width: 0px !important;



  height: 0px !important;



















}











#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {



  padding-top: 25px !important; 



  padding-bottom: 25px !important; 



   -moz-border-radius: 20px !important; 



}











LABEL {



  color: rgba(0,0,0, 100) !important; 



}









#footer {



  display: none !important; 



}







.radar {



  display: none !important; 



}





.selected .following,



.selected .followers,



.selected .settings,



.selected .posts,



.selected .messages,



.selected .drafts,



.selected .queue,



.selected .likes  {



  color: rgba(0, 0, 0, 0.50) !important; 



  background: rgba(0, 0, 0, 0.0) !important; 



}











#new_post .arrow {



  opacity: 1.0 !important;



  margin-left: -1px !important; 



}

.new_post_label{

font-size:14px !important;

padding-top:63px !important;

font-family:courier !important;

font-style:normal !important;

text-transform: lowercase !important;

color:#ffffff !important;

letter-spacing:1px !important

}
}
