css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(dailymotion.com)
{
/* General */
#top_header_announcement,
#footer,
#push_footer,
.dmpi_adsense,

/* Index */
#subheader_container,
.channels,
#home_right_col,
.masscast_box,
#dmpi_video_tools,
.title_owner_rating,
.dmco_html,
#np_more_desc,
#player_page_tabs,

/* User */
.left_content,

/* Videos */
.dmpi_subheader,
#breadcrumb,
.dmpi_subheader,
.video_tools_box,
#right_content_box
{
	display: none !important;
}
#content
{
	width: 660px !important;
	padding-bottom: 0px!important;
}
#wrapper
{
	background: none !important;
}
.dmpi_video_infos{position:relative;}

/* header */
.sd_header ul.header_links,#login_info{
	display: none !important;
}
#header{padding: 0px!important;}
.sd_header{height: 26px!important;}

.sd_video_socialbuttons,
#player_expander,.flag_link,
.user_container img,.owner,.button_green,
#video_views_count,
#search_submit{display:none!important;}

.user_container .button{padding:0px!important;
border:0px!important;
}



.user_container{position:absolute;
top:367px;
right:23px;}
.light_options{z-index:9999;}
.dmpi_video_infos{margin-left: -15px!important;}


#input_search{padding-top:3px!important;
padding-bottom:3px!important;
margin-top:4px!important;
border-top-right-radius: 2px!important;
border-bottom-right-radius: 2px!important;
border-bottom-left-radius: 2px!important;
height: 10px!important;}
.autocomplete{top:-6px!important;
box-shadow:none!important;}

#header,.sd_header{background:#333!important;}

#content h1{
position:absolute;
top:-10px;}

}
