css:
/* Made by http://www.last.fm/user/JayGEEGEEGEE */

@-moz-document url-prefix('http://www.last.fm'),
 url-prefix('https://www.last.fm'),
 url-prefix('http://www.lastfm.fr'),
 url-prefix('http://www.lastfm.jp'),
 url-prefix('http://www.lastfm.de'),
 url-prefix('https://secure.last.fm')
{
    
/*Content for everything*/
.content {
    position: relative !important;
    top: 30px !important;
	box-shadow: 1px 3px 6px 5px #ccc !important;
	border-top: 1px solid #c7ced1 !important;
}

    
/*Logo*/
.lastfm-logo {
	background: none !important;
	background-image: url("http://i.imgur.com/4rCcbkH.png") !important;
	background-repeat: no-repeat !important;
	top: 0px !important
}

.lastfm-logo:hover {
	background: none !important;
	background-image: url("http://i.imgur.com/ouuAbim.png") !important;
	background-repeat: no-repeat !important;
	top: 0px !important;
	transition: background-image 0.218s ease-out 0s !important;
}

/*Log In Page*/
#login-link {
    position: absolute !important;
    top: -13px !important;
	right: 12px !important;
	height: 24px !important;
}
 
a.join {
    position: absolute !important;
    top: -8px !important;
    right: 68px !important;
    height: 24px !important;
    font-weight: bold !important;
    font-size: 12px !important;
}  
    
.intro {
    background-image: none !important;
}
 
.anon-button {
    background: -moz-linear-gradient(bottom, #E3E7E8 0%, #E9ECED 100%) !important;
	background: -webkit-linear-gradient(bottom, #E3E7E8 0%, #E9ECED 100%) !important;
	box-shadow: 0px 3px 3px rgba(0,0,0,0.2) !important;
	text-shadow: 0.1em 0.1em rgba(0, 0, 0, 0.09) !important;
	border-bottom: 1px solid #c7ced1 !important;
	border-top: 1px solid #c7ced1 !important;
	border-right: 1px solid #c7ced1 !important;
	border-left: 1px solid #c7ced1 !important;   
}
    
.artist-grid--anon-home h3 {
    color: #ccc !important;
}

.anon-brand strong {
	text-shadow: 0.1em 0.1em rgba(0, 0, 0, 0.09) !important;
	color: /*[[mycolor]]*/ !important;
}

section.inset.join-call-to-action,
section.inset.up-and-coming {
        background: none !important;
}
    
.chart-item-number {    
	background: -moz-linear-gradient(bottom, #E3E7E8 0%, #E9ECED 100%) !important;
	background: -webkit-linear-gradient(bottom, #E3E7E8 0%, #E9ECED 100%) !important;
	color: /*[[mycolor]]*/ !important;
    text-shadow: 0.1em 0.1em rgba(0, 0, 0, 0.09) !important;
}               

/*Masthead*/
.masthead {
	width: 100% !important;
	position: fixed !important;
	font-size: 12px !important;
	top: 0px !important;
	left: 0% !important;
	right: 0% !important;
	margin: 0px !important;
	background: -moz-linear-gradient(bottom, #E3E7E8 0%, #E9ECED 100%) !important;
	background: -webkit-linear-gradient(bottom, #E3E7E8 0%, #E9ECED 100%) !important;
	height: 21px !important;
	line-height: 17px !important;
	padding-top: 1px !important;
	border-top: none !important;
	box-shadow: 0px 3px 3px rgba(0,0,0,0.2) !important;
	color: transparent !important;
	z-index: 2 !important;
	border-color: #c7ced1 !important;
	border-right: none !important;
	border-left: none !important;
}
     
#fauxHeader {
	display: none !important;
}

#primaryNav .nav-link {
	text-shadow: none !important;
	font-weight: bold !important;
	font-size: 12px !important;
	color: /*[[mycolor]]*/ !important;
}

.primary-nav-link {
	display: inline !important;
}

.primary-nav-link:hover {
	background: none !important;
}

/*Link Colors*/
a {
	color: /*[[mycolor]]*/ !important;
}

a:hover {
	color: /*[[mycolor2]]*/ !important;
    transition: color 0.218s ease 0s !important;
    text-decoration: none !important;
}

/* Home Page */
#page {
	font-family: Helvetica !important;
	font-size: 12px !important;
	color: #393735 !important;
}

body #page {
	width: 990px !important;
	top: 30px !important;
	padding: 0px !important;
}

.page-head {
	background: none !important;
	border-bottom: 0px solid #c7ced1 !important;
	border-top: 0px solid #c7ced1 !important;
	border-right: 0px solid #c7ced1 !important;
	border-left: 0px solid #c7ced1 !important;
	text-shadow: 0.1em 0.1em rgba(0, 0, 0, 0.09) !important;
}

article.content.album-list {
	position: relative !important;
	top: 30px !important;
	box-shadow: 1px 3px 6px 5px #ccc !important;
	border-top: 1px solid #c7ced1 !important;
}


/*Settings Dialog Box*/
.dialogBox {
	background: #e9eced !important;
	border: 1px solid #c7ced1 !important;
	webkit-border-radius: 0px !important;
	border-radius: 0px !important;
	-webkit-box-shadow: 1px 3px 6px 5px #ccc !important; 
	box-shadow: 1px 3px 6px 5px #ccc !important;
}

.modulePreferences h3,
.modulePreferences h3 small {
	color: /*[[mycolor]]*/ !important;
}

/*Profile Page*/
.page-head.separated {
	background: -moz-linear-gradient(bottom, #E3E7E8 0%, #E9ECED 100%) !important;
	background: -webkit-linear-gradient(bottom, #E3E7E8 0%, #E9ECED 100%) !important;
	border-bottom: 1px solid #c7ced1 !important;
	border-top: 0px solid #c7ced1 !important;
	border-right: 0px solid #c7ced1 !important;
	border-left: 0px solid #c7ced1 !important;
}

.shouts .message p {
	color: #393735 !important;
}

.user-badge {
	position: absolute !important;
	top: -13px !important;
	right: 42px !important;
	height: 24px !important;
	border: 0px solid #c7ced1 !important;
	font-size: 14px !important;
	line-height: 16px !important;
}

.subscriber .badgeAvatar .userType a {
	background: #e9eced !important;
	border-bottom: 1px solid #c7ced1 !important;
	border-top: 1px solid #c7ced1 !important;
	border-right: 1px solid #c7ced1 !important;
	border-left: 1px solid #c7ced1 !important;
	color: #393735 !important;
}

#userBadge .badgeAvatar .userType a {
	color: /*[[mycolor]]*/ !important;
}

#userBadge .badgeAvatar .userType a:hover {
	color: /*[[mycolor2]]*/ !important;
	transition: all 0.218s ease-out 0s !important;
	-webkit-transition: all 0.218s ease-out 0s !important;
}

.user-badge:after {
	content: none !important;
}

.user-badge-avatar {
	position: absolute !important;
	left: 10px !important;
}

.user-badge-avatar {
	max-width: 22px !important;
	max-height: 22px !important;
	border: 0px solid #c7ced1 !important;
	border-radius: 0px !important;
}

.tasteometer .super span,
.reading--super .text-over-image-text {
	background: #477085 !important;
}

.tasteometer .veryhigh span,
.reading--very-high .text-over-image-text {
	background: #5d6678 !important;
}

.tasteometer .high span,
.reading--high .text-over-image-text {
	background: #8f9db8 !important;
}

.tasteometer .medium span,
.reading--medium .text-over-image-text {
	background: #555d6e !important;
}

.tasteometer .low span,
.reading--lower .text-over-image-text {
	background: #a3b2d1 !important;
}

img.icon.subscriber_icon {
	background: #555d6e !important;
	background-image: none !important;
}
    

.fiflufi .leftCol {
    background: none !important;
}

#headerLinks,
#headerPromo a {
    display: none !important;
}

#header {
    background: none !important;
    height: 30px !important;
}

body {
    background: #e9eced !important;
}

#profileLinks {
    height: 16px !important;
    line-height: 16px !important;
    top: 0px !important;
    font-size: 11px !important;
}

a.profile-link {
font-weight: bold !important;
    }

#loginLink .profile-link, #loginLink .profile-link strong {
    top: 0px !important;
    background: #646464 !important;
    height: 16px !important;
    line-height: 11px !important;
}

#profileLinks .divider a {
    background: none !important;
}

#idBadgerUser {
    background: transparent !important;
    top: 3px !important;
    position: relative !important;
    font-size: 11px !important;
    height: 14px !important;
    right: 0px !important;
    line-height: 11px !important;
    padding: 0px !important;
    font-weight: bold !important;
    text-shadow: none !important;
}

#idBadgerDropper {
    display: none !important;
}

#idBadgerUser img {
    display: none !important;
}

#idBadgerDropDown {
    background: #e9eced !important;
    border-bottom: 1px solid #c7ced1 !important;
    border-top: 1px solid #c7ced1 !important;
    border-right: 1px solid #c7ced1 !important;
    border-left: 1px solid #c7ced1 !important;
}    
    
/*Charts*/
#recentTracks tr td { 
	background: #e9eced !important; 
	color: #666 !important;
	border-bottom: 1px solid #c7ced1 !important;
	box-shadow: inset 0 1px 0 rgba(255,255,255,0.29) !important; 
}

#recentTracks tr:last-child td { 
	border-bottom: 1px solid #c7ced1 !important; 
}

.responsive table.chart {
	border-spacing: 0px !important;
}

#recentTracks tr.first td { 
	border-top: 1px solid #c7ced1 !important; 
}

#recentTracks tr:hover td { 
	background: #e9e9e9 !important; 
}

#recentTracks span.date {
	color: #777 !important; 
}

#recentTracks tr td.highlight {
	border-top: 1px solid #c7ced1 !important;
	border-bottom: 1px solid #c7ced1 !important;
	background: #e3e3e3 !important;
	box-shadow: inset 0 1px 0 rgba(255,255,255,0.5) !important; 
}

#recentTracks tr td.imageCell { 
	border-left: none !important; 
}

#recentTracks tr td.dateCell {
	padding-right: 8px !important;
	border-right: 1px solid #c7ced1 !important;
}

#recentTracks tr td.dateCell img {
	border-radius: 3px !important; 
}

table.chart tbody tr td { 
	background: #e3e7e8 !important; 
	border-top: 1px solid #c7ced1 !important;  
}

table.chart tbody tr.odd td { 
	background: #e9eced !important; 
}

table.tracklist {
	border-left: 1px solid #c7ced1 !important; 
	border-right: 1px solid #c7ced1 !important; 
}

table.tracklist th.title {
	border-top: 1px solid #c7ced1 !important; 
	border-right: 1px solid #c7ced1 !important; 
}

table.chart tbody tr:hover td,
table.chart tbody tr:hover.odd td { 
	background: #e9e9e9 !important; 
}

table.chart tbody tr td.chartbarCell,
table.chart tbody tr:hover.odd td.chartbarCell { 
	background: none !important; 
}

table.chart tbody tr td .chartbar, 
table.chart tbody tr td .chartbar span {
	background: #c7ced1 !important; 
	color: #477085 !important; 
	font-weight: bold !important; 
	font-size: 10px !important; 
}

table.chart tbody tr:hover td .chartbar,
table.chart tbody tr:hover td .chartbar span { 
	background: #e3e3e3 !important; 
}

table.chart tbody tr td .chartbar { 
	border-radius: 0 0px 0px 0 !important; 
}

table.chart td.positionCell  { 
	color: #333 !important;
}

tr.fdl small a {
	color: #477085 !important; 
}

/*Loved Icon*/
.loved_indicator_icon {
	background: url('http://i.imgur.com/5MShW.png') !important;
	background-repeat: no-repeat !important;
}

/*btns*/
.btn {
	box-shadow: none !important;
	text-shadow: none !important;
}

.btn.btn--track-player {
	background: -moz-linear-gradient(bottom, #E3E7E8 0%, #E9ECED 100%) !important;
	background: -webkit-linear-gradient(bottom, #E3E7E8 0%, #E9ECED 100%) !important;
    border-left: 1px solid #c7ced1 !important;
	border-top: 1px solid #c7ced1 !important;
	border-right: 1px solid #c7ced1 !important;
	border-bottom: 1px solid #c7ced1 !important;
    text-shadow: 0.1em 0.1em rgba(0, 0, 0, 0.09) !important;
}

button.btn.btn--small.btn--brand-blue.iconleft.iconleft--tag.add-tag {
	background: #ced7e1 !important;
    color: #393735 !important;       
} 
    
.btn--primary {
 	color: #393735 !important;
} 

.btn--header {
	background: -moz-linear-gradient(bottom, #E3E7E8 0%, #E9ECED 100%) !important;
	background: -webkit-linear-gradient(bottom, #E3E7E8 0%, #E9ECED 100%) !important;
	font-weight: bold !important;
} 

.btn--header:hover {
	background: -moz-linear-gradient(bottom, #C4C4C4 0%, #EFEFEF 100%) !important;
	background: -webkit-linear-gradient(bottom, #C4C4C4 0%, #EFEFEF 100%) !important;
	color: /*[[mycolor2]]*/ !important;
}

.btn.user-dropdown-toggle {
	position: absolute !important;
	top: -11px !important;
	right: 13px !important;
	height: 24px !important;
	background: none !important;
}

.btn.user-dropdown-toggle:hover {
	background: -moz-linear-gradient(bottom, #C4C4C4 0%, #EFEFEF 100%) !important;
	background: -webkit-linear-gradient(bottom, #C4C4C4 0%, #EFEFEF 100%) !important;
}

a.dropdown-btn-menu-item:hover {
	transition: all 0.218s ease-out 0s !important;
	-webkit-transition: all 0.218s ease-out 0s !important;
}

a.btn--play {
	color: #393735 !important;
}

.user-dropdown {
	border: 1px solid #c7ced1 !important;
	box-shadow: none !important;
}

.user-dropdown .dropdown-btn-menu-item:hover {
	background-color: #c4c4c4 !important;
}

.user-dropdown-divider-bottom {
	border-bottom: 1px solid #c7ced1 !important;
}

.user-dropdown-divider-top {
	border-top: 1px solid #c7ced1 !important;
}

input.dropdown-btn-menu-item.logout-button {
	font-weight: bold !important;
	color: #393735 !important;
}

.dropdown-btn {
	background: #cd7ed1 !important;
	color: #393735 !important;
}

a.ecommerce-btn-text.btn.dropdown-btn,
a.btn.iconleft.iconleft--share.dropdown-btn {
    background: #e3e7e8 !important;
    box-shadow: none !important;
    border-left: 1px solid #c7ced1 !important;
	border-top: 1px solid #c7ced1 !important;
	border-right: 0px solid #c7ced1 !important;
	border-bottom: 1px solid #c7ced1 !important;
}

a.needs-js.btn.dropdown-btn.btn--icon-only.btn--small.menu-toggle.iconright.iconright--dropdown,
A.btn.iconleft.iconleft--tag.btn--small.dropdown-btn.menu-toggle.iconright.iconright--dropdown {
    background: #e3e7e8 !important;
    box-shadow: none !important;
    border-left: 1px solid #c7ced1 !important;
	border-top: 1px solid #c7ced1 !important;
	border-right: 1px solid #c7ced1 !important;
	border-bottom: 1px solid #c7ced1 !important;
}

a.needs-js.btn.dropdown-btn.btn--icon-only.menu-toggle.iconright.iconright--dropdown {
    background: #e3e7e8 !important;
    border-left: 0px solid #c7ced1 !important;
	border-top: 1px solid #c7ced1 !important;
	border-right: 1px solid #c7ced1 !important;
	border-bottom: 1px solid #c7ced1 !important;
}

a.btn.iconleft.iconleft--ecommerce {
    background: #e3e7e8 !important;
    border-left: 1px solid #c7ced1 !important;
	border-top: 1px solid #c7ced1 !important;
	border-right: 1px solid #c7ced1 !important;
	border-bottom: 1px solid #c7ced1 !important;
}

.dropdown-btn-menu {
	background: #e9eced !important;
}

a.btn.btn--on-tour {
	background-color: #e3e7e8 !important;
	color: #393735 !important;
	border-left: 1px solid #c7ced1 !important;
	border-top: 1px solid #c7ced1 !important;
	border-right: 1px solid #c7ced1 !important;
	border-bottom: 1px solid #c7ced1 !important;
} 

btn--brand {
	background-color: #e3e7e8 !important;
	color: /*[[mycolor]]*/ !important;
}

.btn--brand:hover, 
.btn--brand:focus {
	background-color: #e3e7e8 !important;
	color: /*[[mycolor]]*/ !important;
	border-left: 1px solid #c7ced1 !important;
	transition: all 0.3s ease-out 0s !important;
	-webkit-transition: all 0.3s ease-out 0s !important;
}

.user-dropdown .dropdown-btn-menu-item:focus {
	background: -moz-linear-gradient(bottom, #E3E7E8 0%, #E9ECED 100%) !important;
	background: -webkit-linear-gradient(bottom, #E3E7E8 0%, #E9ECED 100%) !important;
	border-top: 1px solid #c7ced1 !important;
	border-bottom: 1px solid #c7ced1 !important;
}

.btn--brand-blue.is-pressed {
    background: -moz-linear-gradient(bottom, #E3E7E8 0%, #E9ECED 100%) !important;
	background: -webkit-linear-gradient(bottom, #E3E7E8 0%, #E9ECED 100%) !important;
	color: /*[[mycolor]]*/ !important;
}

.btn--white {
    background: -moz-linear-gradient(bottom, #E3E7E8 0%, #E9ECED 100%) !important;
	background: -webkit-linear-gradient(bottom, #E3E7E8 0%, #E9ECED 100%) !important;
	color: /*[[mycolor]]*/ !important;
	border-right: 1px solid #c7ced1 !important;
    border-left: 1px solid #c7ced1 !important;
    border-top: 1px solid #c7ced1 !important;
    border-bottom: 1px solid #c7ced1 !important;
}

a.btn.btn--small.btn--toggle-right {
    background: -moz-linear-gradient(bottom, #E3E7E8 100%, #E9ECED 0%) !important;
	background: -webkit-linear-gradient(bottom, #E3E7E8 100%, #E9ECED 0%) !important;
	color: /*[[mycolor]]*/ !important;
	border-right: 1px solid #c7ced1 !important;
    border-left: 0px solid #c7ced1 !important;
    border-top: 0px solid #c7ced1 !important;
    border-bottom: 0px solid #c7ced1 !important;
}

.btn.whittle-pagination-selected {
	background: -moz-linear-gradient(bottom, #E3E7E8 100%, #E9ECED 0%) !important;
	background: -webkit-linear-gradient(bottom, #E3E7E8 100%, #E9ECED 0%) !important;
	color: /*[[mycolor]]*/ !important;
	border-right: 1px solid #c7ced1 !important;
    border-left: 1px solid #c7ced1 !important;
    border-top: 1px solid #c7ced1 !important;
    border-bottom: 1px solid #c7ced1 !important;
}

.dropdown-btn-menu-item.dropdown-btn-menu-item--selected {
	background: -moz-linear-gradient(bottom, #E3E7E8 0%, #E9ECED 100%) !important;
	background: -webkit-linear-gradient(bottom, #E3E7E8 0%, #E9ECED 100%) !important;
	border-top: 1px solid #c7ced1 !important;
	border-bottom: 1px solid #c7ced1 !important;
	color: /*[[mycolor]]*/ !important;
}

/*Tracks Page*/
article.content {
	position: relative !important;
	top: 30px !important;
	box-shadow: 1px 3px 6px 5px #ccc !important;
	border-top: 1px solid #c7ced1 !important;
}

h3.recent-album-title {
    background: -moz-linear-gradient(bottom, #E3E7E8 0%, #E9ECED 100%) !important;
	background: -webkit-linear-gradient(bottom, #E3E7E8 0%, #E9ECED 100%) !important;
	border-left: 0px solid #c7ced1 !important;
	border-top: 1px solid #c7ced1 !important;
	border-right: 1px solid #c7ced1 !important;
	border-bottom: 1px solid #c7ced1 !important;
}

.recent-tracks-list .cover-image {
    border-right: 0px !important;
}

.recent-tracks-item {
   background: #e9eced !important;
   border-right: 1px solid #c7ced1 !important;
   border-left: 1px solid #c7ced1 !important;
   border-top: 1px solid #c7ced1 !important;
   border-bottom: 1px solid #c7ced1 !important;
}

.recent-tracks-item:hover {
    background: #e9e9e9 !important;
}

.recent-tracks-item--album-item:last-child {
    border-bottom: 1px solid #c7ced1 !important;
}

table#deletablert.candyStriped.tracklist {
	border-right: 1px solid #c7ced1 !important;
    border-left: 1px solid #c7ced1 !important;
    border-top: 1px solid #c7ced1 !important;
    border-bottom: 1px solid #c7ced1 !important;
}

table.tracklist tbody td {
    background: #e9eced !important;
    border-bottom: 0px solid #c7ced1 !important;
}

table.tracklist tbody tr:hover td,
table.tracklist tbody tr:hover.odd td { 
	background: #e9e9e9 !important; 
}


/*Music*/
.feedback-message {
    background: -moz-linear-gradient(bottom, #E3E7E8 0%, #E9ECED 100%) !important;
	background: -webkit-linear-gradient(bottom, #E3E7E8 0%, #E9ECED 100%) !important;
	color: #477085 !important;
	text-shadow: 0.1em 0.1em rgba(0, 0, 0, 0.09) !important;
}

/*Tags*/
.tags a, .tag a {
    background-color: #ced7e1 !important;
    color: /*[[mycolor]]*/ !important;
    box-shadow: none !important;
    padding: 4px 5px !important;
    text-shadow: none !important;
}

.tags a:hover, .tag a:hover,
button.btn.btn--small.btn--tag:hover {
    background-color: #e9eced !important;
	color: /*[[mycolor]]*/ !important;
    transition: background-color 0.3s ease-out 0s !important;
    text-shadow: none !important;
}

/*User Listen Counts*/
.user-listen-counts li .chartbar span:hover {
   background: #e9e9e9 !important;
   color: #477085 !important;
   transition: all 0.3s ease-in 0s !important;
   border: 1px solid #c7ced1 !important;
}

/*Shoutbox Expansion*/
input[type="text"] {
    background: #e9eced !important;
    border-color: #d7ced1 !important;
	color: /*[[mycolor2]]*/ !important;
    opacity: 0.5 !important;
    transition: opacity .4s ease-in !important;
    -webkit-transition: opacity .4s ease-out !important;
}

input[type="text"]:focus,
input[type="text"]:hover {
	color: /*[[mycolor]]*/ !important;
    opacity: 1 !important;
    transition: opacity .4s ease-in !important;
    -webkit-transition: opacity .4s ease-in !important;
}

textarea#shoutmsg {
    background: #e9eced !important;
    border-color: #c7ced1 !important;
	color: /*[[mycolor2]]*/ !important;
    opacity: 0.3 !important;
    transition: opacity, height .4s ease-in !important;
    -webkit-transition: opacity, height .4s ease-in !important;
}

textarea#shoutmsg:hover,
