css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document 
domain("www.shanbay.com") 
{

/* 背景 */
body{background: #333333 !important;}
div.hero-unit{background: #222222 !important;}

div.container, div.navbar-inner
{background: -moz-linear-gradient(top, #444444, black) !important;}

div.row td, div.row tr
{background: -moz-linear-gradient(top, #333333, black) !important;border: 0px solid black !important;}

div.quote blockquote, div.alert-info
{background: -moz-linear-gradient(top, #333333, #111111) !important;border: 1px solid black !important;border-radius: 8px 8px 8px 8px !important; padding-top: 5px !important; padding-bottom: 5px !important; margin-bottom: 25px !important; }

div.main-body
{background: -moz-linear-gradient(top, #111111, black) !important;}

/* 窗体背景 */
div.main-body
{background-color: #000000 !important;border-style: none !important;border-radius: 10px 10px 10px 10px !important;margin-top:-14px !important;margin-bottom:6px !important;}

.navbar-inner 
{background-color: #000000 !important;background-image: none !important;}

/* 释义背景 */
div.span9 div.review-progress
{background: -moz-linear-gradient(top, #222222, #444444) !important;border-radius: 10px 10px 10px 10px !important;}

.well
{background: -moz-linear-gradient(top, #00CC66, #008F47) !important;border-radius: 10px 10px 10px 10px !important;}

div#ex-defaults-box ol.span9,div#ex-user-box ol.span9
{background: -moz-linear-gradient(top, #00CC66, #008F47) !important;border-radius: 10px 10px 10px 10px !important;padding-top:10px !important;}

/* 单词字体颜色 */
h1.content
{color: #FF3399 !important;text-shadow: -1px -1px 1px black,1px -1px 1px black,-1px 1px 1px black,1px 1px 1px black,-2px -2px 3px #99FF33,2px -2px 3px #99FF33,-2px 2px 3px #99FF33,2px 2px 3px #99FF33,-3px -3px 3px #FFFF00,3px -3px 3px #FFFF00,-3px 3px 3px #FFFF00,3px 3px 3px #FFFF00 !important;}

p.checkin-award-note, p.default-checkin-note, p.default-checkin-note span, div.checkin_tip_container h3, span.mid,div.quote small, li.dropdown a, a.main-menu, a.shortcuts-button, ul.nav-right li a
{color: #EE3399 !important;font-weight:bolder !important;text-shadow: -1px -1px 1px black,1px -1px 1px black,-1px 1px 1px black,1px 1px 1px black !important;}

/* 音标颜色 */
div.span9 small{color: #66FFFF !important;text-shadow: #66FFFF 0px 0px 0px !important;margin-left:14px !important;}

/* 下一个按钮 */
.continue-button{background-color: #000000 !important;color: #CC66FF !important;font-weight:bolder !important;}

/* 例句按钮 */
.ex-sys-box-tab, .ex-mine-box-tab, .ex-user-box-tab,.note-user-box-tab
{background-color: #99CCFF !important;color: #000000 !important;border-style: none !important;}

/* 例句按钮 */
ul.nav-tabs li.active a
{background-color: #CC99FF !important;color: #000000 !important;border-style: none !important;}

/* 例句字体颜色,h3 浅天蓝色 */
a.option, div.span8 div div a, div.span9,div.quote , div.span9 td , div#choices li span
{color: #99CCFF !important;}

/* 释义字体颜色 */
fieldset#spotcheck-test label, span.content, .text,ol.span7,div.sentence,.step_1,.step_2,.prompt-right,.inactive,.enex,.cnex, .pull-left
{color: #000000 !important;}

.index
{opacity: 0 !important;text-shadow: none !important;}

/* 论坛标题 */
.page-header, div.title,.forgot-btn
{color: #DD6600 !important;}
/* 论坛 the top label */
.sub-nav 
{background-color: #000000 !important;background-image: -moz-linear-gradient(center top , #000000, #000000) !important;background-repeat: repeat-x !important;border: 0px solid #E5E5E5 !important;}

ul.nav-pills
{margin-left:15px !important;margin-right:10px !important;}

.logo a img {opacity:0 !important;   /* dont show the original image */}

/* 论坛 post and number and dakalv */
.pull-right, div.span11 h3, h3.span4
{color: #9494B8 !important;}
/* 论坛 content */
.span5 a
{color: #00FFCC !important;}
/* 论坛 content with bold */
h4.teamname a, div.span7 h4 a
{color: #66CCFF !important;}
/* 论坛 content right part */
ul#forum-box a, div.modal-header h3 small
{color: #DD0000 !important;font-weight:bolder !important;}

div.span6 a, div.span5 a
{color: #000000 !important;font-weight:bolder !important;}

ul#forum-box li.active a, div.modal-header h3, div#level-setting h3, div#level-setting label
{color: #000000 !important;font-weight:bolder !important;}
/* next page button text color */
div.pull-right a
{color: #990099 !important;font-weight:bolder !important;}
/* 论坛 user and forum */
span.user a,span.forum a, div.content span,div.pull-left div.author
{color: #339900 !important;}
/* 序号 */
span.counter
{color: #FF8533 !important;}

div.start-review 
{background: -moz-linear-gradient(top, #FF6600 , #B24700) !important;}

div.promotion, div#choices span.btn, div#choices button.btn
{background: -moz-linear-gradient(top, #9966FF , #6B47B2) !important;}

small, span.unit, button.btn, div.btn-group a
{color: #AA0000 !important;}

div.row h4, div#choices span.btn, div#choices button.btn, div.cndf a, div.well a
{color: #99BB33 !important;text-shadow: -1px -1px 1px black,1px -1px 1px black,-1px 1px 1px black,1px 1px 1px black !important;}

div.actions a, div.pull-right a
{color: #DDDD00 !important;text-shadow: -1px -1px 1px black,1px -1px 1px black,-1px 1px 1px black,1px 1px 1px black !important;}

div.row td, div.row td div, div.row h1.span2 span, div.row h1.span2 small, div.row h2 small
{text-shadow:-1px -1px 1px #000000,1px -1px 1px #000000,-1px 1px 1px #000000,1px 1px 1px #000000 !important;}

div.row h1 small, div.row h2 small, h1.span2 span
{color: #00DD00 !important;}

button,a,td{text-shadow: #0000ff 0px 0px 1px !important;}span{text-shadow: #0033ff 0px 0px 1px !important;}div {text-shadow: #0033ff 0px 0px 1px !important;}p,dd,pre,h2,h3,font,b {text-shadow: #0033ff 0px 0px 1px !important;}
/* words list style with purple */
div.span9 td{background: -moz-linear-gradient(top, #050505, #442277, #050505) !important}
}
