css:
/* 
YouTube watch page uncluttered

Last update: January 2015

Originally from http://userstyles.org/styles/75661

*/

@-moz-document url-prefix("http://www.youtube.com/watch"), url-prefix("https://www.youtube.com/watch")
{
#watch-discussion > *, /* user comments */
#watch-related > .video-list-item:nth-child(n+9), /* reduces video suggestions */
#yt-masthead-signin, /* Upload/Sign in button*/
#footer-container, /* Footer */
#watch-action-panels, /* share video after watching */
.watch-action-buttons, /* Add to, Share, Rating */
#watch7-subscription-container, /* Subscribe */
#appbar-guide-button, /* Dropdown next to YouTube logo */
#watch7-user-header a.yt-user-photo /* User profile pic */{
display:none !important;
}
#watch-discussion:after
{
content: "Comments for this video have proudly been disabled ;-)";
font-size: xx-large !important;
color: grey !important;
}
}
