css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tudou.com") {

body, div.page_content, div.frame_2cols
{
background-color: #222222 !important;
}

div.extra, div.conwrap
{
background-color: #000000 !important;
}

div.g-mini-play, div.g-mini
{
background-color: #222222 !important;
background-image: none !important;
}

/* 标题颜色 */
div.g-mini-nav a, div.g-mini-user a {
     background-color: #222222 ! important;
     color: #00FF00 ! important;
     font-size: 13px ! important;
}
/* 更多背景颜色 */
div.viewRecord_item, div.g-user-nav ul, div.sub_tp, div.more-top, div.g-more-nav, div.viewRecord_do,div.sub_ft {
     background-color: #222222 ! important;
     background-image: none !important;
}
/* 标题红色 */
div.g-more-nav h3 {
     color: #FF0000 ! important;
}
div.vct h4 {
     color: #FF0000 ! important;
     font-size: 16px ! important;
}

/* Dark */
div#relatives, div.auto {
     background-color: #222222 ! important;
     border-radius: 10px 10px 10px 10px ! important;
     padding-left: 3px ! important;
     padding-right: 3px ! important;
}

/* Dark */
ul.relative_sub, div#relatives li.current a{
     background-color: #222222 ! important;
}

div.cmt_content{
     color: #CC99FF ! important;
     font-size: 13px ! important;
}

/* Cyan */
p.cmt_hd a, div.hd h4, ul.tl b, div.h ul.t a
, div.rank li.ri span.m, div.h span.ap a
, div.hot10 li.ri a, div.c a, div.frame_2cols a
{
     color: #00FFFF ! important;
}

/* Red */
p.cmt_fd a, div.txt h6 a, div.c dt
{
     color: #FF0000 ! important;
}

/* Purple */
ul.l a, li.ri a, div.txt a
{
color: #CC66FF !important;
}

/* Grass Green */
ul.t-sub a, ul.info a
{
color: #66FF33 !important;
}

div.h h2 a, div.h h2 span,div.h h2
{
color: #66FF33 !important;
font-weight: bolder !important;
text-shadow: 
1px 1px 1px #800000, 
-1px 1px 1px #800000, 
1px -1px 1px #800000, 
-1px -1px 1px #800000,
2px 2px 1px #800000, 
-2px 2px 1px #800000, 
2px -2px 1px #800000, 
-2px -2px 1px #800000
!important;
}

/* Orange */
li.current a, ul.tl a, ul.info li, div.h ul.t li.current a
{
color: #FF6600 !important;
}

div.c, div.h ul.t, div.h ul.t a, div.tdrama, div.hot-board
, div.sbg, div.mb, div.sb, div.p, div.g_subnav
, div#adex_seed, div.pack_video_card, div.b
{
background-color: #111111 !important;
}

div.h, div.h h2, div.h ul.t-sub a, div.h h2
{
background-color: #333333 !important;
}

}
