css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("deviantart.com") {

/*SUBMIT ICON*/
#oh-menu-submit .oh-l:before
{
position: relative;
display: inline-block;
content: '';
width: 16px;
height: 18px;
top: -2px;
margin-right: 5px;
vertical-align: top;
background: url('http://st.deviantart.net/minish/main/more.gif') -163px -2px;
/*background: url('http://st.deviantart.net/minish/main/more.gif') -1963px -2px;*/
}

/*SHOP ICON*/
#oh-menu-shop .oh-l:before
{
position: relative;
display: inline-block;
content: '';
width: 17px;
height: 18px;
top: -2px;
margin-right: 5px;
vertical-align: top;
background: url('http://st.deviantart.net/minish/main/more.gif') -42px -1px;
}

/*MESSAGES ICON*/
#oh-menu-split i.i3
{
background: url('http://st.deviantart.net/minish/messages/mc3.gif') -476px 40px no-repeat !important;
/*background: url('http://st.deviantart.net/minish/gruzecontrol/icons-gruser.gif') -1317px 40px no-repeat !important;*/
}

/*MESSAGES ICON INSIDE MENU (to avoid two identical icons above each other)*/
.oh-menuctrl i.i13
{
background: url('http://st.deviantart.net/minish/gruzecontrol/icons-gruser.gif') -1321px 2px no-repeat !important;
}

/*FRIENDS ICON*/
#oh-menu-friends i.i2
{
background: url('http://st.deviantart.net/minish/gruzecontrol/icons-gruser.gif') -882px 40px no-repeat !important;
/*background: url('http://st.deviantart.net/minish/gruzecontrol/icons-small-modules.gif') -318px 40px no-repeat !important;*/
}

/*FAVOURITES ICON*/
#oh-menu-collect i.i1
{
background: url('http://st.deviantart.net/minish/gruzecontrol/icons-gruser.gif') -1120px 40px no-repeat !important;
}

/*STUFF THAT HIDES THE "Deviant" TEXT*/
#oh-menu-deviant .oh-darker
{
display: none;
}
#oh-menu-deviant .oh-darker.oh-symbol
{
display: inline;
}

/*USER (FELLA) ICON*/
#oh-menu-deviant .oh-darker.oh-symbol:before
{
position: relative;
display: inline-block;
content: '';
width: 18px;
height: 18px;
top: -2px;
margin-right: 4px;
vertical-align: top;
background: url('http://st.deviantart.net/minish/gruzecontrol/icons-small-modules.gif') -1160px -1px;
/*background: url('http://st.deviantart.net/minish/main/more.gif') -124px -1px;*/
}

/*ENJOY!*/

}
