/* try 0 */

/* general ************************************************************/
body {
    /*acolor: black !important;
    Abackground: #EFEDEF !important;
    abackground: #F0F0F0 !important;*/
    font-family: Verdana, Geneva, Arial, Helvetica, sans-serif;
    /*awidth: 1950px;*/
    width: auto !important;
}

#left-col {
    width: 100% !important;
}

#right-col, #breaking, #highlights, #mastfoot{
    display:none !important;
}

.container {
    width: 100% !important;
}

.container #main-outer {
    background: #101010 !important;
    width: auto !important;
    /*aawidth: 950px;*/
    margin: 0px auto;
}

/* remove social-buttons */
#main-inner .social-buttons {
    display: none;
}

/* comments ***********************************************************/
.comment {
    background: #F5F6FB !important;
    border-left: 1px dotted #B7C7E2 !important;
    border-bottom: 1px solid #A7B7D2 !important;
    border-bottom-width: 0 !important;
}

/* head ***************************************************************/
.comment .meta {
    background: #A7B7D2;
}

.comment .user {
    color: white !important;
    font-weight: bold !important;
    font-size: 1.0em !important;
}

.comment .anchor {
    color: black !important;
    /*afloat: right !important;*/
}


/* remove comma 
.comment .meta:nth-child(3) {
    display: none !important;
}


.comment .meta a > * {
    display: none;
}

.comment .meta {
    Adisplay: none;
}
*/
.comment .meta .date{
    display: inline;
}

.comment .date {
    color: white !important;
    /*font: normal !important;*/
    font-weight: normal !important;
    font-size: 0.9em !important;
    float: right !important;
    padding-right: 15px !important;
}
/* body ***************************************************************/
.comment .body p {
    color: black !important;
    font-family: Verdana, Geneva, Arial, Helvetica, sans-serif !important;
    background: #F5F6FB;
}

.comment .AAbody:after {
    font-size: 10%;
    content: "*";
    color: #F5F6FB;
    background: #05F6FB;
}

/* bottom *************************************************************/
.comment .controls {
    background: #F5F6FB;
}

