@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(happypancake.com){

/* ---------------------HEADER---------------------------*/

/*hide upper banner*/
.top-banner{
    display:none !important;
}

/*center header*/
#header{
    width:716px !important;
}

/*center main content*/
.wrapper, .main, #header, #footer{
    margin:0px auto !important;
}

/*hide right banner - make sure links are clickable*/
#hp_sticky{
    display:none !important;
}

}
