css:
@-moz-document url-prefix("http://www.tumblr.com/"), url-prefix('https://www.tumblr.com/')   {
div.l-header .tab.iconic > a {
background-image: url('http://i269.photobucket.com/albums/jj74/BlueDarkness3/TF2_bar.png');
}

body{
background: #2C4762 url('http://i269.photobucket.com/albums/jj74/BlueDarkness3/blue_teutone.png') top repeat-x;
background-color: #768a88;
color: #fdf2d7;
}

#logo {
background: url(http://i269.photobucket.com/albums/jj74/BlueDarkness3/mann_logo.png) no-repeat !important;
}

#footer {
color: #34302d;
}
#footer a {
color: #34302d;
}

#posts .post .arrow {
background-image: url('http://i269.photobucket.com/albums/jj74/BlueDarkness3/dashboard_master_sprite_tf2.png');}

#posts .post .permalink {
background-image: url('http://i269.photobucket.com/albums/jj74/BlueDarkness3/dogear_TF2.png');
}

div.l-header .selection_nipple {
border-bottom: 12px solid #34302d;
border-bottom-color: #34302d;
}

#posts .post .conversation_lines .chat_line {
border-right: 1px solid #34302d;
border-bottom: 1px solid #34302d;
border-left: 1px solid #34302d;
}

#posts .post .conversation_lines .chat_line:first-child {
border-top: 1px solid #34302d;
}

#posts .post .image_thumbnail {
background-color: 34302d;
-webkit-box-shadow: 0 1px 4px #34302d;
-moz-box-shadow: 0 1px 4px #34302d;
box-shadow: 0 1px 4px #34302d;
}

#posts .post .conversation_lines .chat_line:nth-child(even) {
background-color: #34302d;
}
}

@-moz-document url-prefix("http://www.tumblr.com/new"), url-prefix("http://www.tumblr.com/reblog") , url-prefix("https://www.tumblr.com/new"), url-prefix("https://www.tumblr.com/reblog") {
button.positive, button.negative, button.generic, input.generic {
border: solid 2px #34302d;
}

body {
font-family:tf2 build;
color: #6b6a65;
}
}

@-moz-document url-prefix("http://www.tumblr.com/dashboard"), url-prefix("http://www.tumblr.com/inbox"), url-prefix("http://www.tumblr.com/blog"), url-prefix("http://www.tumblr.com/likes") , url-prefix("https://www.tumblr.com/dashboard"), url-prefix("https://www.tumblr.com/inbox"), url-prefix("https://www.tumblr.com/blog"), url-prefix("https://www.tumblr.com/likes") {


.new_post_label .new_post_label_icon {
background-image: url('http://i269.photobucket.com/albums/jj74/BlueDarkness3/post_icons_edited.png');
}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background-color: #34302d;
}

#posts .post {
background-color:#395c78;
}


a {
color: #f6b98a;
}


#posts .post.new_post .new_post_label {
font-weight: normal;
text-align: center;
color: #ffffff;
font-size: 28px;
font-family:tf2 professor;
}

#right_column .controls_section li a {
font-family:tf2 build;
font-size: 12px;
font-weight: normal;
}

#right_column::after {
position: absolute;
top: 100%;
right: 0;
left: 0;
height: 35px;
content: ' ';
background: -moz-linear-gradient(top,rgba(52, 48, 45, 1) 0,rgba(52, 48, 45, 0) 100%);
background: -webkit-gradient(linear,left top,left bottom,color-stop(0%,rgba(52, 48, 45, 1)),color-stop(100%,rgba(52, 48, 45, 0)));
background: -webkit-linear-gradient(top,rgba(52, 48, 45, 1) 0,rgba(52, 48, 45, 0) 100%);
background: -o-linear-gradient(top,rgba(52, 48, 45, 1) 0,rgba(52, 48, 45, 0) 100%);
background: -ms-linear-gradient(top,rgba(52, 48, 45, 1) 0,rgba(52, 48, 45, 0) 100%);
background: linear-gradient(top,rgba(52, 48, 45, 1) 0,rgba(52, 48, 45, 0) 100%);
}
#posts .post .post_avatar {
background: #395c78;
}

h2 .optional {
color: #6b6a65;
}

#pagination a {
color: #f6b98a;
background-color: #395c78;
}

#posts .post .post_info a {
color: #f6b98a;
font-family:tf2 secondary;
}

#posts .post .post_question_asker {
color: #f6b98a;
}

#posts .post .post_question {
background: #fdf2d7;
}

.source_url .source_url_gradient {
position: absolute;
top: 0;
bottom: 0;
left: 0;
width:0;
}

#posts .post .post_question_nipple {
background-image: url('http://i269.photobucket.com/albums/jj74/BlueDarkness3/dashboard_master_sprite_tf2.png');
}
}
